/* AGENT_SHEET */
@-moz-document domain("wikipedia.org"), domain("userstyles.org"), domain("userscripts.org"), domain("j-lyric.net"), domain("hudong.com"), domain("ku6.com"), domain("newsrumble.tw"), domain("gamme.com.tw"), domain("blogspot.com"), domain("chinaiiss.com"), domain("ptt.cc"), domain("disp.cc"), domain("mapledramas.com"), domain("powerapple.com"), domain("funshion.com"), domain("javfree.me") {
* { font-family: "Microsoft Yahei",sans-serif !important; }
a,a:link,a:visited,a:active,a:hover { text-decoration: none !important; }
html, body {background: #121212 !important; color: #939392 !important; }
body * { background-image:none !important; }
body div, body span, body h1, body h2, body h3, body input, body button, body table, body tr, body td, body th,textarea,a,nav,#search,#view-code,#related,li,ul,#catalog
 {
border-color: #939392 !important;
background-color: #121212 !important;
color: #939392 !important;
background-image:none !important;
 }

body input, body input *, body button { background: #696969 !important; color: #121212 !important; }
body img { border: 0 !important; zoom:100% !important;background-color: #cccccc !important;}
body a { color:#E0BC2D !important; }
body a:link { color:#E0BC2D !important; }
body a:visited { color:#728896 !important; }
body a:hover { color:#939392 !important; }
body a:active { color:#423F3F !important; }
lightbox * { background-color:transparent !important; }

html {zoom:110%  !important; }
.author-styles td { font-size:14px  !important;  line-height:160%  !important;  border-bottom:2px solid #939392 !important; }
pre { font-size:24px  !important;  line-height:160%  !important;  border-bottom:2px solid #939392 !important; }
#finds,#mainContent {  width:80% !important; }
#discussions-area { display : none !important; }
}
