css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fr.ogame.gameforge.com"), regexp("https?://[^/]*ogame.gameforge.com/.*")
{

#playerName{margin-left: 30px !important;}

#rechts #myPlanets .smallplanet {    height:51px !important;}

body#movement #planet, body#fleet2 #planet, body#fleet3 #planet, body#messages #planet, body#network #planet, body#networkkommunikation #planet,   body#networkverwaltung #planet, body#networkbewerbungen #planet {

   height:40px !important;
}

#resources #resourceSettings, #den {    display:none !important;}
#resources #details22 {    display:none !important;}
#resources #buttonz {    height: 230px !important;}

#overview #planetdata {    margin-top: 21px !important;}
#overview .c-left, #overview .c-right {    top: 118px !important;}

#overview #planet {

    height: 154px !important;
    background-position: 0px -146px !important;
}

#eventFooter  {    display:none !important;}

body#overview #planetdata{margin-top: 0 !important;}
body#overview .c-left, body#overview .c-right {display: none !important;}

body#overview, body#alliance, body#defense, body#resources, body#station, body#station-moon, body#shipyard, body#research, body#highscore, 
body#traderOverview, body#premium, body#shop, body#messages, body#galaxy, body#fleet1, body#fleet2, body#fleet3, body#movement, body#preferences,
#info, body[id] #planet {

    background-image:none !important;
}

#message-wrapper{

    background: repeat scroll 0 0 transparent !important;
}

body#movement #planet, body#fleet2 #planet, body#fleet3 #planet, body#messages #planet{

    height:40px !important;
}
#galaxyLoading, #techDetailLoading, #statisticsLoading {background: transparent;}

#planetdata .overlay {    height: 0% !important;}             
#eventboxLoading img, #star, #star1, #star2 { display:none !important; }
#fleet2 #planet { display:none !important; }

#changelog_link,#helper,#countColonies { display:none !important; }

.status_abbr_honorableTarget{color :white !important}
.status_abbr_inactive{color :yellow !important}
.status_abbr_longinactive{color :yellow !important}

#probes,#probeValue,#recycler,#recyclerValue,#rockets,#missileValue,#slots,#slotValue,#slotUsed{color :lime !important}

.anti_planets_construction, .anti_planets_constructionMoon {color :red !important}
html body#preferences.lang-fr div.contentBoxBody div#boxBG div#box div#contentWrapper div#inhalt div#planet {    display:none !important;}

.build-faster { display:none !important; }

#ago_panel { display:none !important; }
#ago_icon { display:none !important; }

body#fleet1 .firstcol  { display:none !important; }

.ago_fleet_infos { display:none !important; }
.ago_fleet_read { display:none !important; }
#ago_fleet_continue_5 { display:none !important; }
#ago_fleet_continue_9 { position:relative !important; top:-70px; left:-50px  !important; }

body#fleet1 #ago_shortcuts { display:none !important; }

#overview #planet { display:none !important; }

#overview #overviewBottom{ display:none !important; }
#myPlanets{position:fixed !important; }
    
    
#messages #netz #showSpyReportsNow{margin: 0px 30px !important; }
#netz .contentz table td.actions{ padding: 5px 0px 5px 0px !important; }
}
