@-moz-document domain("mail.google.com"), url-prefix("https://mail.google.com/mail"), url-prefix("http://mail.google.com/mail") {

/* iOS Scrollbars */
scrollbar {
  -moz-appearance:none!important;
  background:transparent!important; 
  margin:2px 0 2px 0!important; 
  border:0!important;
  border-radius:7px!important;
  -moz-transition-property:opacity!important;
  -moz-transition-duration:0.5s!important;
  -moz-transition-delay:0s!important;
}
scrollbar[orient='vertical'] {min-width:9px!important; max-width:9px!important;}
scrollbar[orient='horizontal'] {margin-bottom:2px!important; height:9px!important; min-height:9px!important; max-height:9px!important;}

scrollbarbutton {display:none!important;}
scrollbar gripper {background:transparent!important;}

﻿﻿﻿thumb, scrollbar:hover thumb, slider:not(:hover) thumb {
  -moz-appearance:none!important; 
  background:rgba(0,0,0,0.4)!important; 
  border:1px solid rgba(85,85,85,0.6)!important; 
  border-radius:7px!important; 
  opacity:0.75!important;
  -moz-transition-property:all!important; 
  -moz-transition-duration:0.5s!important; 
  -moz-transition-delay:0s!important;
  -moz-transition-timing-function:ease-in!important;
}

slider {
  -moz-appearance:none!important;
  background:#f0f0f0!important; 
  border-radius:7px!important; 
  border:0!important; 
  opacity:0;
  -moz-transition-property:opacity!important; 
  -moz-transition-duration:0.5s!important; 
  -moz-transition-delay:0s!important;
  -moz-transition-timing-function:ease-in!important;
}
slider:hover {
  opacity:0.9!important; 
  -moz-transition-property:opacity!important; 
  -moz-transition-duration:0.5s!important; 
  -moz-transition-delay:0s!important;
  -moz-transition-timing-function:ease-out!important;
}
slider[orient='vertical'], thumb[orient='vertical'] {min-width:6px!important; max-width:6px!important;
}
slider[orient='horizontal'], thumb[orient='horizontal'] {min-height:6px!important; max-height:6px!important;
}


/* TRUE HOVER ACTIVATION */
.nn .oy8Mbf > [class='T0 akj pp'] scrollbar[orient='vertical'],
.nn .oy8Mbf > [class='T0 akj pp']:hover scrollbar:not(:hover),
.nn .oy8Mbf > [class='T0 akj pp']:hover scrollbar:hover,
.nn .oy8Mbf > [class='T0 akj pp']:hover scrollbar:hover:active,
.nn .oy8Mbf > [class='T0 akj pp']:hover scrollbar:active:not(:hover)

 {margin-right:3px!important; padding-right:1px!important;}

.ajm:hover slider:not(:hover),
[class*='ajl aib lKgBkb']:hover slider:not(:hover),
.nn .aeJ:hover slider:not(:hover),
.nn .oy8Mbf > [class='T0 akj pp']:hover slider:not(:hover)
 {
  opacity:0.3!important;
  -moz-transition-property:opacity!important;
  -moz-transition-duration:0.5s!important;
  -moz-transition-delay:0s!important;
 }

.aj9:hover slider:hover, .ajm slider:hover,
.aj9:hover slider:hover:active, .ajm slider:hover:active,
.aj9:hover slider:active:not(:hover), .ajm slider:active:not(:hover),

.nn .aeJ:hover slider:hover,
.nn .aeJ:hover slider:hover:active,
.nn .aeJ:hover slider:active:not(:hover),

.nn .oy8Mbf > [class='T0 akj pp']:hover slider:hover,
.nn .oy8Mbf > [class='T0 akj pp']:hover slider:hover:active,
.nn .oy8Mbf > [class='T0 akj pp']:hover slider:active:not(:hover)
 {
  opacity:1!important;
  -moz-transition-property:opacity!important;
  -moz-transition-duration:0.2s!important;
  -moz-transition-delay:0s!important;
 }
 
/* MAKING iOS SEAMLESS */
div[class*='akc lKgBkb'] > div:first-child + div[class*='T0 pp saH2Ef'],
.adC .anT + .Pj + .nH, .z0DeRc, .u5 {display:none!important;}
.nH.MC {display:none!important; position:absolute!important;}
.nH.if, .nH.if .nH.hx {padding-bottom:0!important; margin-bottom:-1px!important;}

[class='nH oy8Mbf aeN'] {padding-right:0!important; max-width:170px!important;}
.ajm {width:170px!important;}
[class='r9gPwb KgCXA'] {width:160px!important; padding-right:0!important;}

[class='l2 ov'] {margin-bottom:10px!important;}
.aj9.pp {width:170px!important;}
.aj9.pp .oo {width:160px!important;}
[class*='lKgBkb'] scrollbar {margin:auto 0!important;}
.nn .aeJ {margin-right:-5px!important; padding-right:5px!important;}
.nn .oy8Mbf > [class='T0 akj pp'] {overflow-x:hidden!important; height:93.35%!important;}

/* === FOR RIGHT-SIDE CHAT === */
table[class='Bs nH iY'] .Bu + .Bu + .Bu, table[class='Bs nH iY'] .Bu + .Bu + .Bu:not(:hover) {
background-color:#fff!important;
position:absolute!important; 
top:50%!important; 
right:140px!important; 
opacity:0.05;
}
table[class='Bs nH iY'] .Bu + .Bu + .Bu:hover {
background-color:#fff!important;
opacity:1.0!important; 
z-index:10!important;
-moz-transition-property:background-color,opacity!important;
-moz-transition-duration:0.5s!important;
}
[class='nH adC'], [class='nH adC'] *, .Bu > .Bt {background-color:#fff!important;}
[class='nH adC'] > .nH > .anT > .nH + .nH {display:none!important;}﻿

}
