css:
@namespace url(http://www.w3.org/1999/xhtml);@-moz-document url('https://www.tumblr.com/login'),url('http://www.tumblr.com/dashboard'),url('http://www.tumblr.com/new/blog'),url('http://www.tumblr.com/inbox'),url('http://www.tumblr.com/help'),url('https://www.tumblr.com/preferences'),url('https://www.tumblr.com/lookup'),url('http://www.tumblr.com/explore'),url('http://www.tumblr.com/following'),url('http://www.tumblr.com/likes'),url-prefix('http://www.tumblr.com/new/'),url-prefix('http://www.tumblr.com/blog/'),url-prefix('http://www.tumblr.com/reblog/'),url-prefix('http://www.tumblr.com/tumblelog/'),url-prefix('http://www.tumblr.com/tagged/'),url-prefix('http://www.tumblr.com/spotlight/'),url-prefix('http://www.tumblr.com/edit/'),url-prefix('http://www.tumblr.com/dashboard/')
, url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/new/blog"), url("https://www.tumblr.com/inbox"), url("https://www.tumblr.com/help"), url("https://www.tumblr.com/explore"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/likes"), url-prefix("https://www.tumblr.com/new/"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/reblog/"), url-prefix("https://www.tumblr.com/tumblelog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/spotlight/"), url-prefix("https://www.tumblr.com/edit/"), url-prefix("https://www.tumblr.com/dashboard/") {
/*sets text color for ALL text*/
/*
* {
  color:rgba(255, 255, 255, 1.0) !important;
}
*/

/*BIG BACKGROUND begin - background for the entire screen*/
body {
  background:url("http://i41.tinypic.com/f0py0w.jp") top center repeat fixed !important;
}
/*BIG BACKGROUND end*/

/*LOGO begin - tumblr logo*/
#logo {height: 0 !important;
        width: 0 !important;
	padding-left: 240px !important;
	padding-top: 70px !important;
	background: url('http://i41.tinypic.com/2e4e0jn.jpg') no-repeat !important;}
/*LOGO end*/

/*ICON BAR begin - new post icons bar - graphic replacement*/
.new_post_label .new_post_label_icon {background:none !important;}
#new_post{
  background-image: url('http://i43.tinypic.com/2wqwghf.jpg') !important;
  background-position: center center !important; 
  background-repeat: no-repeat !important;
}
/*ICON BAR  end*/

/*ICON BAR BACKGROUND begin - background for the icon bar*/
#new_post {
  background-color: rgba(255, 255, 255, 1.0) !important;
}
/*ICON BAR BACKGROUND end*/

/*AVATAR ARROW begin - arrow to the left of the icon bar*/
#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}
/*AVATAR ARROW end*/

/*CONTENT begin - background for posts, icon bar, sidebar*/
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(255,255,255, 0.20) !important;
}
/*CONTENT*/ 

/*POST BACKGROUND begin*/
#posts LI[class*=" post"] {
  background: rgba(255, 255, 255, 1.0) !important;
}
/*POST BACKGROUND end*/

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 11px rgba(255,255,255,1.0) !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px rgba(255,255,255, 0.20)  !important;
}
/*SELECTION ARROW end*/

/*RADAR begin - removes radar blue frame - softens appearance*/
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;
}

.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}

/*RADAR end*/

/*NEW CONTENT begin - text post message body background*/
.mceContentBody {
  color: rgba(0, 0, 0, 1.0) !important;
  background: rgba(255,255,255, 1.0) !important;
}
/*NEW CONTENT end*/



.permalink {
  background: none !important;
  width: 16px !important;
  height: 16px !important;
  background: rgba(170,179,188, .85) !important;
  border-bottom-left-radius: 10px !important;
  border-top-right-radius: 24px !important;
  margin-top: 6px !important;
  margin-right: 6px !important;
}
.permalink:hover {
background: rgba(152,143,127, 1.0 ) !important;
}

.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}
.post.photo_post .photo, .post.photo_post .video_thumbnail {
  -moz-box-shadow: 2px 2px 2px rgba(0,0,0, 0.50) !important;
}
.content.is_media, .mask.top, .mask.bottom {
  background: none !important;
}

#left_column #posts .notification {
  background-color:#ffffff !important;
  color:#000000 !important;
  border-bottom-color: #C0C0C0 !important;
  border-top-color: #ffffff !important;
  border-right-color: #ffffff !important;
  border-left-color: #ffffff !important
  
}
#left_column #posts .notification .block {
  color:#ffffff !important;
}
#left_column #posts .notification.alt {
  background-color: #ffffff !important;
}
#left_column #posts .notification.last_notification,
#left_column #posts .notification.single_notification {
  border-bottom-color:#ffffff !important;
}
#left_column #posts .notification a {
  color:#000000 !important;
  border-bottom:0px !important;
}
#left_column #posts .notification blockquote {
  border-left-color:#ffffff !important;
}
#left_column #posts .notification .avatar {
  background-color:#ffffff!important;
  opacity:1 !important;
}
#left_column #posts .notification .avatar:hover {
  opacity:1 !important;
}

.nipple {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(255,255,255, 1.00) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 0px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 0px !important;
}
.nipple.border {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  border-right: solid 6px rgba(255,255,255, 1.0) !important;
  border-bottom: 6px solid transparent !important;
  border-left: 1px solid transparent !important;
  border-top: 6px solid transparent !important;
  margin-top: 1px !important;
}

*{
  text-shadow:none !important;
}

IMG[src="http://assets.tumblr.com/images/content_top_edit_form.png?alpha"],
IMG[src="http://assets.tumblr.com/images/content_bottom_edit_form.png?alpha"] {
  display: none !important;
}

#posts .post .permalink {width:23px;height:23px;background:url('http://i43.tinypic.com/2sbn7k3.jpg') !important;}

#posts .notification .notification_type_icon {background:url('http://i40.tinypic.com/2hdbio8.jpg') !important;}
#posts .notification .notification_type_icon.follow_icon{width:14px;height:13px;background-position:-700px 0 !important;}
#posts .notification .notification_type_icon.like_icon{right:10px;top:11px;width:13px;height:12px;background-position:-700px -20px !important;}
#posts .notification .notification_type_icon.reblog_icon{right:10px;top:9px;width:14px;height:14px;background-position:-700px -40px !important;}
#posts .notification .notification_type_icon.answer_icon{width:10px;height:16px;background-position:-700px -60px !important;}
#posts .notification .notification_type_icon.reply_icon{top:11px;width:14px;height:16px;background-position:-700px -80px !important;}

li.selected,li.recessed,a.active,div.post_filter_tabs a{background:none!important}

H1,.title{color:#000000 !important;}

/*DO NOT DELETE THIS BRACKET*/}
