css:
@-moz-document domain("irccloud.com") {

#buffer div.scroll span.date
{
float: left;
font: inherit;
margin-right: 10px;
width: 40px;
}

#buffer div.scroll span.author
{
width: auto;
margin: 0;
padding: 0;
font: inherit;
}

#buffer div.scroll span.author A
{
 color: #04c;
}

#buffer div.scroll div.messageRow
{
padding-left: 0;
font-size: 12px;
}

#sidebar
{
left: 0;
top: 0px;
}

#bufferView
{
margin: 0 0 0 200px;
}

#buffer div.scroll div.highlight span.message
{
color: red !important;
}

#buffer div.scroll div.highlight
{
background-color: inherit;
}

#statusBar
{
margin-left: 0;
padding-right: 20px;
}

#buffer div.scroll div.messageRow span.message
{
color: #555;
}
#users h2
{
display: none;
}

#users ul
{
padding: 0 !important;
border: 0 !important;
background-color: transparent !important;
}

#users li ul A
{
 color: #666 !important;
}

#users li.ops ul A
{
 color: #f44 !important;
}

#users li.voiced ul A
{
 color: #0a0 !important;
}

#users li .away
{
opacity: 0.4;
}
}
