img[src="http://www.strangebeaver.com/wp-content/uploads/2011/11/picture.jpg"], img[src="http://strangebeaver.com/sticker.jpg"], body > div:nth-of-type(3), div#BAP-holder, div#sidebar1.sidebar, div#sidebar2.sidebar, div#footerwrap, p.tags, div.sociable, div#___plusone_1, div#skyscraper, div#lws_0, table {display:none !important;}
}

@-moz-document url-prefix("http://www.strangebeaver.com/gallery") {
div#MENUS, embed#Siber_embed1, body > div:nth-of-type(2) {display:none !important;}
}
