@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wrzuta.pl") {

#main #right,
#content .files_box .box_content .files .user,
#footer,
#login_me_contener,
#content #tags_in_header,
#header #user_info #header_upload,
#content .movie-wrap #file-header-wrap #social-header-wrap,
#file_info_nav #file_info_nav_top,
#content #action_file,
#content #file_menu .file_desc #user_info_data_time,
#content #playlist_info_links,
#content #file_info_links,
#content #file_info #user_info_links,
#content .file-actions,
#content .player-ctrls-margin,
#content #comments_form,
#content #comments_list,
#content #comments,
#content .file-report-link,
#ajax_comments,
#share_catalogue_fb,
.file .tags,
#content #found_files_container .files .user,
.quick_actions_wrap,
#user_menu_home_wrap,
#content .playlist_name_header #sharethis-container,
.fb_like,
#content .files-promo,
#main .file-details,
.nav-header,
.user-links
{
	display: none !important;
}

#content,
#content .main_page_audio_box .files li .title,
#content .movie-wrap #file-header-wrap #file-header,
#content #file_details_info,
#content ul#file_menu li#description,
#content #found_files_container .files_box .audio .thumbnail,
#content #found_files_container .files_box .audio .title
{
	width: auto !important;
	float: none !important;
}

#content .files_box .main_page_box .files li,
#content #found_files_container .popnew .files .file
{
	width: 239px !important;
	text-align: center !important;
}

#content .files_box .main_page_box .files li .thumbnail,
#file_info_nav,
#content #found_files_container .files .file .thumbnail
{
	display: block !important;
	width: auto !important;
}

#content .main_page_audio_box .files li
{
	width: 230px !important;
	text-align: center !important;
}

#content .main_page_audio_box .files li .info,
#content .files .info
{
	clear: none !important;
}

#content .movie-wrap #file-header-wrap #file-header .header,
#content ul#file_menu li#description,
#content #found_files_container .files .file,
#content .movie-wrap .header,
#main .file-title
{
	text-align: center !important;
}

#file_info_nav #file_info_nav_next
{
	float: right !important;
}

#click_to_make_smaller
{
	top: 0 !important;
}

#filters_option
{
	left: 546px !important;
}

.header-inner {
    width: 680px !important;
}

#content ul.spis li,
#content #found_files_container .files .file
{
	width: 181px !important;
	text-align: center !important;
}

#content #found_files_container .files_box .audio .thumbnail,
#content #found_files_container .files_box .audio .thumbnail img,
#content #video-player-container
{
	margin: 0 auto !important;
}

}
