css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("time.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, font, strong, p, form, footer

{
color: white !important;
background: black !important;
}

select, button, #nav-top, #vertical-nav, .dbrf .dbrf2, .sec-search-main .search-mag, .ul-search-tabs li a.on, .ul-search-tabs li a

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}


/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code, #vertical-nav li.sel a, #vertical-nav li.sel a:link, #vertical-nav li.sel a:visited, #vertical-nav li.sel a:active

{
background: #333333 !important;
}

#vertical-nav, #footer-logo, #moreInside

{
border-top: 1px solid black !important;
}

#vertical-nav, #stayConnected-new

{
border-bottom: 1px solid black !important;
}

}
