css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {







{



  text-shadow:none !important;

  color:#373737 !important;



}







Body {



  background-color:#e3bd90 !important;

  color:#373737 !important;

  font-size:12px !important;

  background-image: url('http://26.media.tumblr.com/tumblr_lv4cc824f81qj7pdmo1_r1_500.jpg') !important;

  background-repeat:no-repeat !important;

  background-attachment: fixed !important;

  background-position:bottom left !important



}



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {



 background: rgba(209,178,115, .40)!important;

  



}


ol#posts li.post.new_post {

background: #ffffff url('http://25.media.tumblr.com/tumblr_lv5f76GQhd1qj7pdmo1_r6_500.jpg') top center no-repeat !important;

}






.new_post_label {

font-size: 11px !important;

padding-left: 1px !important;

font-weight: bold !important;

font-family: Times New Roman, seans-serif!important;

text-transform: uppercase !important;

color: #000000 !important;

letter-spacing: 1px !important

}



#logo {height: 0 !important;



        width: 0 !important;



	padding-left: 240px !important;



	padding-top: 70px !important;



	background: url(http://29.media.tumblr.com/tumblr_lv4dw33IxH1qj7pdmo1_r2_250.jpg) no-repeat !important;}



*{text-shadow:none !important; color:#565656 !important;}







.tag_page_header {

width: 870px !important;

height: 55px;

}





#content_top, #content_bottom  {

width: 870px !important;

height: 20px !important;

background: transparent !important;

display: none;

}


#footer {

background: rgba(209,178,115, .40) !important;

color: #efefef !important;

background-image: none !important;

padding: 5px !important;

}



#footer a {

color: #efefef !important;

}





#pagination a {

color: #60400e !important;

text-decoration:none;

background-color: #ececec !important;

padding:5px 8px;

margin-left:10px;

white-space:nowrap

}



#auto_pagination_loader img { display: none; }





#auto_pagination_loader {

	text-shadow: none 2px 1px 0px;


background: transparent!important;

border: 0px!important;

color: #ffffff;







}









#pagination a:hover {

color: #000000 !important

}





img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"] {

width:0;

height:70px;

padding-right:250px;

opacity: 0;

}




#left_column #posts .notification {

background-color: #d3c093 !important;

color: #444444 !important;

border-bottom-color: #ffffff !important

}


#left_column #posts .notification.alt {

background-color: #f2f4e7 !important;

border-bottom-color: #ffffff !important

}



#left_column #posts .notification a {

color: #60400e !important;

border-bottom:0px !important;

}




#left_column #posts .notification blockquote {

border-left-color: #60400e !important;

color: #333333 !important;

}





#left_column #posts .notification .block {

color: #60400e !important;

}







#left_column #posts .notification.last_notification, #left_column #posts .notification.single_notification {

border-bottom-color: #ffffff !important;

}







#left_column #posts .notification .avatar {

border-color:transparent !important;

background-color: #fbfbfb !important;

opacity:0.5 !important;

}





#left_column #posts .notification .avatar:hover {

opacity:1 !important;

}















h1,h2,h3,h4,h5,h6 {

margin-top: 5px !important;

color: #444444 !important

}



#edit_post_controls .advanced_option .inline_input_label {

display: none;

}



#edit_post_controls .advanced_option select, #edit_post_controls .advanced_option input, #edit_post_controls .advanced_option textarea {

color: #666666;

padding-top: 5px;

padding-right: 5px !important;

padding-bottom: 5px;

padding-left: 5px;

margin-top: 10px !important;

}



#edit_post_controls .advanced_option {

padding-top: 15px !important;

padding-right: 0pt;

padding-bottom: 15px;

padding-left: 0pt;

}







#edit_post_controls {

margin-left: 15px !important;

width: 200px !important;

}






#edit_post_controls #set_privacy.advanced_option #post_state {

width: 200px !important;

}





#edit_post_controls #set_date.advanced_option #post_date.text_field {

width: 190px !important;

}




#edit_post_controls #set_source_url.advanced_option #post_source_url.text_field {

width: 190px !important;

}





#tag_editor {

width: 190px !important;

height: 70px;

}





#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



  background-color:#f4dcaa !important;

  background-image:none !important;

  border:0px !important



}



#nav .nav_item .nav_menu {



  border-bottom:0px !important



}



#nav .nav_item .nav_menu a {



  color:#888 !important;

  border-top-color:#eee !important;



}



#nav .nav_item .nav_menu a:hover {



  color:#666 !important;



}





.no_posts_found, h1.dashboard_header, h1.dashboard_header a, h1.dashboard_header span {



  color:#ccc !important



}



#right_column {

float: right !important;

width: 200px !important;

}







#right_column .dashboard_nav_item {

border-bottom: 0px solid #eee !important;

background-image: none  !important;

}











#right_column .dashboard_nav_item .dashboard_nav_title, #right_column .dashboard_nav_item .dashboard_nav_title a, #right_column .dashboard_nav_item a, #right_column .dashboard_nav_item ul.dashboard_subpages, #right_column .dashboard_nav_item ul.dashboard_subpages li a {

color: #642e2c !important;

}











#right_column a#dashboard_switch_blog_menu_current {

position: relative;

display: block;

height: 25px !important;

width: 190px;

font-size: 13px !important;

color:#ececec  !important;

}










#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {

color: #cecece !important;

}









#right_column .dashboard_nav_item .dashboard_controls_radar_media {

overflow-x: hidden;

overflow-y: hidden;

}




#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background-color: #6d0b13 !important;

margin-right: 10px !important;

padding-bottom: 5px;

padding-left: 5px;

}




#right_column .dashboard_nav_item .dashboard_controls_radar_media .post img {

max-width: 175px !important;

}







#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {

background: none !important;

}







#left_column {

float: left;

width: 620px !important;

border: 0px !important;

}







#left_column #posts .notification .avatar {

border-color: transparent !important;

background-color: #ececec !important;

opacity:0.4 !important;

}


#posts .post .post_question {

background-color: #d3c093 !important;

color: #3e3e3e !important;

}







#left_column #posts .notification .avatar:hover {

opacity:0.6 !important;

}





.post, #posts {

opacity:1 !important;

border-bottom:0px !important;

color: #222222 !important

}







.post:hover {

opacity:1 !important

}







#posts .post .permalink, .permalink {

background-color: transparent !important;

background-image: none !important;

}



.active {

background-color: #6d0b13 !important;

}



#right_column .dashboard_nav_item a {

color: #ececec !important;

}



.dashboard_switch_blog_menu_header {

  background-color:#fbfbfb !important;

  color:#666 !important;



}



.dashboard_switch_blog_menu_item {

  background-color:#fbfbfb !important;

  color:#666 !important;

  border:0px !important



}



.dashboard_switch_blog_menu_footer {

  background-color:#fbfbfb !important;

  color:#666 !important;



}



.dashboard_switch_blog_menu_wrapper {

  background-color:#fbfbfb !important;



}



#new_post_notice_container {

  color:#fff !important



}



#new_post_notice_container a {



  color:#fff !important



}



#dashboard_switch_blog_menu_current,

#dashboard_switch_blog_arrow_and_divider,

#dashboard_switch_blog_arrow_current {

  background-image:none !important



}



.sub_avatar{width:32px !important; height:32px !important; background-color:transparent !important}

.post.photo_post .photo, .post.photo_post .video_thumbnail {

  -moz-box-shadow: 2px 2px 2px rgba(209,178,115, .40) !important;

  border-radius: 12px !important;



}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;



}



.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 12px rgba(209,178,115, .40) !important;

  border-right: 12px solid transparent !important;

  border-left: 12px solid transparent !important;



}



.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 12px rgba(209,178,115, .40)  !important;



}



.post.note.same_user_as_last.is_mine:hover,.post.regular.is_mine:hover,.post.regular.same_user_as_last.is_mine:hover,.post.link.not_mine:hover,.post.quote.not_mine:hover,.post.audio.not_mine:hover,.post.video.not_mine:hover,.post.regular.not_mine:hover,.post.note.not_mine:hover,.post.photo.not_mine:hover {



  border-top-right-radius: 24px !important; 

  opacity: 1.00 !important;



}



.permalink {



  background: none !important;

  width: 16px !important;

  height: 16px !important;

  background: rgba(209,178,115, .40) !important;

  border-bottom-left-radius: 10px !important;

  border-top-right-radius: 24px !important;

  margin-top: 6px !important;

  margin-right: 6px !important;



}



.permalink:hover {



background: rgba(209,178,115, .40 ) !important;



}



#right_column > UL > LI:nth-child(n):not([class^="item"]),

#right_column > UL:nth-child(n) > DIV > DIV:nth-child(n) > A:nth-child(n){



  background-image: none !important;

  background:rgba(0,0,0,0.0) !important; border:0px !important;}



.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, .selected .tag, #post_two, #categories A:last-child {



   border-bottom-left-radius: 6px !important;

   border-bottom-right-radius: 6px !important;



}



.text_field, #post_state, #tag_editor, .source_url_questionmark_gradient, .mceLayout, .mceToolbar.mceCenter.mceFirst.mceLast, .mceIframeContainer.mceFirst.mceLast, #left_column DIV:nth-child(11), #post_one, #video_upload, .customize_button, #categories A:first-child, .selected .following, .following, .selected .posts, #left_column DIV:nth-child(9) {



   border-top-left-radius: 6px !important; 

   border-top-right-radius: 6px !important;



}

}
