css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.heise.de/tp/") {

  /* Werbung an störenden Positionen ausblenden */
  .top-ad {
    display: none !important;
  }

  .pos-ad-high {
    display: none !important;
  }

  .ad-high {
    display: none !important;
 }

 .spalte-ad {
    display: none !important;
  }

  /* zentrieren und farbe anpassen */
  .mainframe {
    float: none !important;
    margin: auto !important;
  }

  .main {
    left: 125px !important;
    top: 80px !important;
    border-bottom: 45px solid #888888 !important;
  }

  .men-1 {
    left: 125px !important;
    top: 36px !important;
  }

  .tp-footer {
    left: 0px !important;
  }

  body {
    background-color: #888888 !important;
  }

}

@-moz-document url-prefix("http://www.heise.de/tp/foren") {

  /* keine korrektur, wenn im forum */
  .main {
    left: 0px !important;
    top: 0px !important;
  }

  .men-1 {
    left: 0px !important;
    top: 0px !important;
  }

  /* dafür andere zentrierung */
  .maintable {
    float: none !important;
    margin: 30px auto auto !important;
  }
}

@-moz-document url-prefix("http://www.heise.de/tp/blogs/foren") {

  /* keine korrektur, wenn im forum */
  .main {
    left: 0px !important;
    top: 0px !important;
  }

  .men-1 {
    left: 0px !important;
    top: 0px !important;
  }
  
    /* dafür andere zentrierung */
  .maintable {
    float: none !important;
    margin: 30px auto auto !important;
  }
}
