@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("asphalto.org") {

table[quoted_user="/*[[utente1]]*/"]:not(.mainpost), 
table[quoted_user="/*[[utente1]]*/"] .main_post_text {height:/*[[H1]]*/px;}
table[quoted_user="/*[[utente2]]*/"]:not(.mainpost), 
table[quoted_user="/*[[utente2]]*/"] .main_post_text {height:/*[[H2]]*/px;}
table[quoted_user="/*[[utente3]]*/"]:not(.mainpost), 
table[quoted_user="/*[[utente3]]*/"] .main_post_text {height:/*[[H3]]*/px;}

table[quoted_user="/*[[utente1]]*/"] .post_text, 
table[quoted_user="/*[[utente1]]*/"] .main_post_text {background-image:url(/*[[img1]]*/);} 
table[quoted_user="/*[[utente2]]*/"] .post_text, 
table[quoted_user="/*[[utente2]]*/"] .main_post_text {background-image:url(/*[[img2]]*/);} 
table[quoted_user="/*[[utente3]]*/"] .post_text, 
table[quoted_user="/*[[utente3]]*/"] .main_post_text {background-image:url(/*[[img3]]*/);} 

table[quoted_user="/*[[utente1]]*/"] .post_text, 
table[quoted_user="/*[[utente1]]*/"] .main_post_text, 
table[quoted_user="/*[[utente2]]*/"] .post_text, 
table[quoted_user="/*[[utente2]]*/"] .main_post_text, 
table[quoted_user="/*[[utente3]]*/"] .post_text, 
table[quoted_user="/*[[utente3]]*/"] .main_post_text  {background-repeat:no-repeat; background-position:center; font-size:0px !important;}

table[quoted_user="/*[[utente1]]*/"] .post_text *,
table[quoted_user="/*[[utente1]]*/"] .main_post_text *, 
table[quoted_user="/*[[utente2]]*/"] .post_text *,
table[quoted_user="/*[[utente2]]*/"] .main_post_text *, 
table[quoted_user="/*[[utente3]]*/"] .post_text *,
table[quoted_user="/*[[utente3]]*/"] .main_post_text * {display:none;}

}
