css:
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document domain("imo.im") {
/*New mods*/
div.blist-container{
    font-size:12px !important;
}
.buddyitem{
    border-bottom:0px solid!important;
}
div.bar.white, div.bar.white.collapsed{
   border-top:1px solid!important;
   color:white!important;
   background-color:transparent!important;
}
/*end of new mods*/

.convlog *, #buddypanel{
   color:white!important;
   background-color: transparent !important;
 }
/*Remove salmon pink selection when searching contacts*/
.buddyitem.selected{
   background: none !important;
}
/*Active buddy*/
.buddyitem.active {
   background-color: #101010 !important;
   opacity:0.7;
}
/*Hover on buddy*/
.buddyitem:hover {
   background-color: #0084DB !important; /*original blue 0B97D3*/
   opacity:0.7;
}
/*Remove gloss*/
.bar{ 
  opacity:0.8;
  border-width:1px 0 1px 0 !important; /*new*/
}

/*Your name color in chat*/
.me  .na {color:#94d8f5!important}
/*Buddy name color in chat*/
.bk0 .na {color:#0B97D3!important;}
/*Buddy background color in chat*/
.bk0  {background:rgba(0,0,0,0.25) !important}
/*Online and offline message color in chat*/
.sys_offline, .sys_online {background:rgba(0,0,0,0.25) !important;}
.sys_offline .sys * {color:red!important}
.sys_online .sys * {color:#00EB00!important}
/*URL color in chat*/
.ms a {text-decoration:underline!important}
.ms a:visited {color:darkviolet!important}
.ms a:hover {color:#94d8f5!important}
/*Send Button hover effect*/
.primary-button:hover {
  background-image:none!important;
  background-color:#6DD0F7!important;
  box-shadow: inset 1px 1px 0px #4EA6CC ;
  border-radius:1px;
}
/*No yellow Applications button*/
#play{background-color: #e0e0e0!important;}
#play:hover {background-color: #f0f0f0!important;}
/*Play button hover effect*/
.game_play_btn:hover{
  background-image:none!important;
  background-color:#9DE98E!important;
  box-shadow: inset 1px 1px 0px #66DD50 ;
  border-radius:1px;
}
#imo #body #main, .convlog, #buddypanel{
   background-image: url(data:image/jpeg;base64,)!important;
}
}
