css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
===== CSS Information ====================================

creat        2008/03/13 - maplebread
lastupdate 2008/03/13 - maplebread
Ver          1.0.1
URL          http://my.opera.com/oddeye/

==========================================================
*/
@-moz-document domain("twitter.com"),url-prefix(http://twitter.com), url(http://twitter.com/home) {
table tr.hentry td.content,p{
padding-left:5px !important;
}

.entry-title entry-content{
padding-left:200px !important;
}

table tr.hentry td.content strong{
width:100px !important;
display:block !important;
float:left !important;
}

table tr.hentry td.content:hover,p:hover{
background-color:#ddd !important;
}
p.entry-title entry-content{
float:left !important;
}


html {
min-height:101% !important;
height: 101% !important;
margin:0px 10px !important;
}

* {
font-size:14px !important; /* font-size */
line-height: 1.6 !important;  /* line-height */
font-weight:normal !important;
background:none !important;
border:none !important;
margin-left:0px !important;
position:static !important;
top: auto !important;
right: auto !important;
margin:0px !important;
padding:0px !important;
color:black !important;
}

#header,#profile-image,.tabMenu,#dim-screen,#accessibility, .section-header,#friends,#vcard,#footer,hr,.statuses_options,#navigation{
display:none !important;
}

tr.hentry td,#content,#container,#side{
width:auto !important;
}
p{
display:inline !important;
}

p.meta entry-meta{
font-size:12px !important
}

#side{
float:left !important;
border-bottom:1px solid gray !important;
width:100% !important;
}

.info{
float:left !important;
border:1px solid #eeeeee !important;
margin-bottom:20px !important;
}

#follow-control{
border-bottom:1px solid #eeeeee !important;
}

input.update-button{
display:block !important;
float:left !important;
margin-left:10px !important;
}


}
