@-moz-document url-prefix("http://jin115.com/") {

  header#header > :not(#logo),
  div#side_left,
  div#side_right,
  div#contents > div#archive_title,
  div#contents > div#new-tab,
  div#contents > div[align="center"],
  div#container + div,
  footer#footer
  {display:none !important;}

  div.index_article_header > h2 > a{font-size: 18px !important;}

  nav#top-header{width:780px !important;}
  nav#top-header > ul.submenu{display: none !important;}
  
  body{
    min-width:100% !important;
    background-image:none !important;
  }
  
  div#dummy_left,
  div#dummy_right
  {margin:0px !important;}
  
    header#header,
    div#logo
    {height:40px !important;}
    div#logo img{
       width:240px !important;
       height:50px !important;
    }

  
    header#header{width:780px !important;}
    div#container{width:774px !important;}
    div#contents_left{width:780px !important;}
    div#contents{width:780px !important;}
  
  
}


@-moz-document url-prefix("http://jin115.com/archives/") {


  article#contents > div#new-tab,
  div.footer_social_ad,
  div#hot_tweet,
  div#popular_articles_comment,
  div#other_news_website,
  div.jin-ads
  {display:none !important;}


    div.article{width:750px !important;}

    div#top-container{width:100% !important;}

    ul#out_button{width:300px !important;}



}
