css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

/* userstyles.org - Dark */
/* Created by JohnTuley */
/* http://userstyles.org/users/69668 */

body { background-color: #000000; }
a { color: #ff3300 !important; text-decoration: none !important; }
a:hover, #show-search-options:hover { text-shadow: 0 0 3px #ff3300; }
label { color: #ff3300; }
pre { background-color: #121212 !important; border: 1px dashed #252525 !important; }
nav { background-color: #202020 !important; }
p, code, li, h2, h3, #more-screenshots, #miscellaneous-info, #no-discussions, #main-article { color: #ffffff; }
#show-search-options { color: #ff3300; text-decoration: none !important; }
#main-header { background-color: #404040 !important; color: #ffffff }
#long-description { color: #ffffff; }
#search, #related, #install { background-color: #202020 !important; }

}
