css:
body { 
background-color: transparent !important; 
background-image: url("http://img256.imageshack.us/img256/5629/rarbanner.jpg") !important;
background-attachment: fixed !important;
background-repeat: no-repeat !important;
background-position: top right !important;
background-color: #cc99ee !important;
color: #BABABA !important;
     }






#contentCol  { background-color:#F8F3FF !important; }
.profile .profile_color_bar  { background-color:#F8F3FF !important; }



#blueBar       { background:#B678F0 !important; }
#pageLogo a    { background:url(http://i.imgur.com/46nnp.png) no-repeat scroll -21px 0 !important; }
#headNavOut    { background:#CFB2F6 !important; border-color:#7D26DE !important; }
	



.jewelToggler  { background-image:url(http://i.imgur.com/46nnp.png) !important; }
#jewelAlert:hover, #jewelAlert:focus, #jewelAlert:active,
#jewelMail:hover, #jewelMail:focus, #jewelMail:active,
#jewelRequest:hover, #jewelRequest:focus, #jewelRequest:active   { background-color:#CFB2F6 !important; }
#jewelCase .jewel  { border:none !important; }




#navSearch button  { background:url(http://i.imgur.com/46nnp.png) no-repeat scroll -1px 0 !important; }
#navSearch     { border-color:#B678F0 !important; }




#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#7D26DE !important; color:#ffffff !important; }




.lightblue_box  { background-color:#EDE2FC !important; border-color:#CFB2F6 !important; }
.commentable_item .ufi_section  { background-color:#EDE2FC !important; }
textarea, .inputtext, .inputpassword  { border-color:#CFB2F6 !important; }




a  { color:#7D26DE !important; }
.uiLinkButton input  { color:#2c2c2c !important; }
.action_links_title .comment_link, .action_links_bottom .comment_link, .feedback_show_link, .feedback_hide_link, .UIActionLinks .comment_link  { color:#2c2c2c !important; }
button.as_link  { color:#2c2c2c !important; }




.uiSideNav .selected .item, .uiSideNav ul .selected .subitem, .uiSideNav .selected .item:hover, .uiSideNav ul .selected .subitem:hover  { background-color:#CFB2F6 !important; }

.uiSideNav .item:hover, .uiSideNav .subitem:hover  { background-color:#EDE2FC !important; }
.friends_online_sidebar li.buddy_row a:hover  { background-color:#EDE2FC !important; }

.unread  { background-color:#CFB2F6 !important; }
.line  { color:#7D26DE !important; }




#jewelCase .jewelFooter a   { background-color:#EDE2FC !important; }
#jewelCase .jewelFooter .uiTextSubtitle  { color:#7D26DE !important;  }

#jewelCase .jewelFooter a:hover span, #jewelCase .jewelFooter a:active span, #jewelCase .jewelFooter a:focus span, #jewelBoxMail a:hover .uiTextSubtitle, #jewelBoxMail a:active .uiTextSubtitle, #jewelBoxMail a:focus .uiTextSubtitle  { color:#7D26DE !important; }

#jewelBoxMail li a:hover, #jewelBoxMail li a:active, #jewelBoxMail li a:focus  { background-color:#EDE2FC !important; border-color:#B678F0 !important; }
#jewelCase .jewelBox  { background-color:#F8F3FF !important; }




.uiHeader h2  { color:#7D26DE !important; }




.current_stuff td  { background-color:#F8F3FF !important; border-color:#CFB2F6 !important; }




.UIComposer .UIComposer_Box  { background-color:#F8F3FF !important; border-color:#CFB2F6 !important; }
.profile .box .box_header  { background-color:#F8F3FF !important; border-color:#CFB2F6 !important; }




.profile .top_bar ul.tabs li.selected a.tab_link  { background-color:#ffffff !important; border-color:#CFB2F6 !important; }
.profile .top_bar ul.tabs li a.tab_link  { background-color:#CEB0F6 !important; border-color:#B678F0 !important; }
.profile .top_bar ul.tabs li a.tab_link:hover  { background-color:#7D26DE !important; color:#ffffff !important; border-color:#7D26DE !important; }





#chat_tab_bar .disabled .chat_window .chat_conv  { background-color:#F8F3FF !important;  }
#chat_tab_bar .chat_header  { background-color:#B678F0 !important; border-color:#000000 !important; }
#chat_tab_bar .chat_header_name a  { color:#ffffff !important; }
#chat_tab_bar .chat_window .chat_conv .visibility_change  { background-color:#CFB2F6 !important; color:#7D26DE !important; }
#presence_bar .presence_menu_opts h2 span  { background:#B678F0 !important; }
#presence_bar .presence_menu_opts h2  { border-color:#B678F0 !important; }
.buddy_list a.friend:hover, .buddy_list a.selected, .buddy_list a.drag  { background:#F8F3FF !important; }

#presence.fbx_bar .presence_section, #presence.fbx_bar #chat .tab_handle, #presence.fbx_bar #presence_gk_tab, #presence.fbx_bar #presence_translations_tab  { background:#B678F0 !important; border-color:#B678F0 !important; }
#presence .presence_bar_button .inner_button  { border-top:none !important; }
#presence #buddy_list_tab #buddy_count .buddy_count_num  { color:#ffffff !important; }

#presence .presence_menu_opts  { border-color:#B678F0 !important; }




.UIButton_Blue  { background:#7D26DE !important; border-color:#B678F0 !important; }
.UIComposer_InputArea  { border-color:#CFB2F6 !important; }
.uiButtonConfirm  { background:#7D26DE !important; border-color:#B678F0 !important; }
#presence #buddy_list_tab #buddy_count strong  { color:#ffffff !important; }
