css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {
body {
	font: 12px/24px tahoma !important;
}
a {
	color: #21759b !important;
	font-family: tahoma !important;
	text-decoration: none !important;
}
.body {
	font-family: tahoma !important;
}
.l_subscribe,.stats, .hideopen, .attach a, input, #infomessage, .sharebox .title textarea, .sharebox td, .label, .value {
	font: 12px/24px tahoma !important;
	font-weight: normal !important;
}
.sharebox, .entry, .pager.top {
	border-bottom: none !important;
}
.entry .title {
	background-color:  /*[[titlebgcolor]]*/;
	border: 1px solid #dbdbdb !important;
	border-radius: 5px;
	padding: 8px !important;
}
.entry .title .text {
	font: 12px/24px tahoma !important;
	text-align: justify !important;
}
.foaf {
	float: left;
}
.entry .name {
	direction: ltr;
	font-size: 11px;
	text-align: right;
}
.entry .info {
	background-position: left !important;
	font-size: 11px !important;
	padding: 5px !important;
	padding-left: 15px !important;
	text-align: left !important;
}
.entry .likes {
	background-position: left !important;
	font-size: 11px !important;
	padding-left: 20px !important;
	text-align: left !important;
}
.entry .comment {
	font: 11px/22px tahoma !important;
	text-align: justify;
}
.entry .comment.friend a {
	color: #008000 !important;
}
.entry .comment.owner a {
	color: #FF0000 !important;
}
.entry .commentform {
	padding-right: 20px !important;
}
.entry .commentform textarea {
	font-family: tahoma !important;
	font-size: 11px !important;
}
.box-bar-text a {
	color: #fff !important;
}
.subscribebar a {
	color: #e4e4e4 !important;
}
.entry .info a.service {
	background: #ff8221;
	border: 1px solid #ff6400;
	border-radius: 5px;
	color: #fff !important;
	font-size: 11px !important;
	font-weight: bold !important;
	padding: 3px;
}
.entry .info a.service[href*=twitter\.com] {
	background: #9ae4e8;
	border: 1px solid #33ccff;
	color: white !important;
}
.entry .info a.service[href*=youtube\.com] {
	background: #ff3232;
	border: 1px solid #ff1919;
	color: white !important;
}
.entry .info a.service[href*=delicious\.com] {
	background: #3274d1;
	border: 1px solid #3248d1;
	color: #000 !important;
}
.entry .info a.service[href*=google\.com\/reader] {
	background: #fff;
	border: 1px solid #3292ff;
	color: #3292ff !important;
}
.entry .info a.service[href*=wakoopa\.com] {
	background: #7ded02;
	border: 1px solid #eb1cc6;
	color: #eb1cc6 !important;
}
.entry .info a.service[href*=last\.fm] {
	background: #d9322a;
	border: 1px solid #ff1919;
	color: #fff !important;
}
.entry .info a.service[href*=flickr\.com] {
	background: #fff;
	border: 1px solid #ff0084;
	color: #0063dc !important;
}
.entry .info a.service[href*=friendfeed\.com] {
	background: #b0ccef;
	border: 1px solid #437ec7;
	color: #437ec7 !important;
}
.entry .info a.service[href*=tumblr\.com] {
	background: #35526e;
	border: 1px solid #437ec7;
	color: #eceff0 !important;
}
.entry .info a.service[href*=vi\.sualize\.us] {
	background: #171716;
	border: 1px solid #31beff;
	color: #31beff !important;
}
}
