css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(del.icio.us) {
	* {
		font-family: Verdana !important;
	}
	.post {
		border:1px solid #D0DFE4 !important;
		margin:-1px 10px 0 0 !important;
		padding:15px 15px 15px 15px !important;
		width:700px !important;
	}
	.post:hover {
		background: #F5FBFF !important;
	}
	.desc a {
		font-size: 10pt !important;
		font-weight: bold !important;
		color: #145CA6 !important;
	}
	.meta {
		font-size: 9pt !important;
		margin-top: 4px !important;
	}
	.tag {
		color: #145CA6 !important;
		margin:0 6px 0 0 !important;
	}
	.pop {
		background: none !important;
		color: #555555 !important;
		margin-left: -20px !important ;
	}
	.date {
		color: #555555 !important;
		padding-left: 20px !important;
	}
	.commands {
		font-size: 9pt !important;
		color: #111111 !important;
	}
	.commands a {
		font-size: 9pt !important;
		color: #555555 !important;
	}
	#crumb {
		font-size: 16px !important;
	}
	.auth-h1, .unauth-h1 {
		margin: -5px 0 0px 10px !important;
		color: #666666 !important;
		font-size: 16px !important;
	}
	.auth-h1 a, unauth-h1 a {
		color: #78B82B !important;
	}
	#header-user-links {
		padding: 6px 10px 6px 10px !important;
		margin-left: 10px !important;
		background-image:  url("data:image/gif;base64,%3D%3D") !important;
		background-repeat: repeat-x !important;
	}
	#header-user-links a {
		color: #FFFFFF !important;
		background: #3E71C0 !important;
	}
	#header-auth-links {
		margin: -28px 0 0 0 !important;
	}
	#header-global-links {
		margin: -10px 308px 0 0 !important;
	}
	.search-box {
		margin: -37px 0 0 0 !important;
	}
	#infobar {
		margin:-10px 15px 0 15px !important;
		padding:0 0 0 0 !important;
	}
	#page-desc {
		margin:6px 0 0 15px !important;
		padding:0 0 0 0 !important;
	}
	#sidebar {
		width:235px !important;
	}
	.sidebar-inner {
		border:1px solid #D0DFE4 !important;
		margin-top: 6px !important;
		padding:0 0 0 0 !important;
		background-color: #FFFFFF !important;
		width:235px !important;
	}
	.sidebar-inner a {
		color: #145CA6 !important;
	}
	#sidebar h3 {
		background: #DBEEFF !important;
		color:#6D6D6D !important;
		margin:0 0 0 0 !important;
		padding:3px 3px 3px 3px !important;
		width:230px !important;
	}
	#sidebar UL LI UL LI {
		padding-left:10px !important;
	}
	.cloud a:not([href^="/settings/"]):not([href^="?set"]) {
		font-size:9pt !important;
	}
	.cloud a:not([href^="/settings/"]):not([href^="?set"]):not([class="one"]) {
		font-size:10pt !important;font-weight:bold !important;
	}
	#main P a[href^="http://del.icio.us/"] {
		font-size: 12pt !important;
		font-weight: bold !important;
		padding: 5px 15px 8px 15px !important;
		color: #000000 !important;
		background: #D9E1F7 !important;
	}
	.pager {
		margin: -10px 0 0 0 !important;
		padding:5px 0 5px 15px !important;
		background: #DBEEFF !important;
		width:717px !important;
	} 
}
