css:
@-moz-document url-prefix('http://twitter.com'), url-prefix('http://twitter.com/*'), url-prefix('https://twitter.com'), url-prefix('https://twitter.com/*')  {

body { background : url('http://twitter.com/images/themes/theme14/bg.gif') repeat !important; }
div#page-container { background-color: #111 !important; }
a { color: #777 !important; }

/* left hand */
div.main-content { background-color: #222 !important; }
div.page-header { background-color: #222 !important; }
div.tweet-box-title h2 { text-shadow: none !important; }
textarea.twitter-anywhere-tweet-box-editor {
	color: #DDD !important;
	background-color: #000 !important;
	box-shadow: none !important;
}
div.profile-header { background-color: transparent; }
div.profile-actions { background-color: #222 !important; }
div.tweet-box { background-color: #222 !important; }
div.stream {
	background-color: #111 !important;
	color: #777 !important;
}
div.stream-tabs { background-color: #111 !important; }
ul.stream-tabs { border-bottom: 1px solid #333 !important; }
ul.stream-tabs li.active a {
	background-color: #111 !important;
	border: 1px solid #333 !important;
	border-bottom: 1px solid #111 !important;
}
ul.stream-tabs li.active { border-bottom: 1px solid #111 !important; }
.tab-text { text-shadow: none !important; }
div.stream-title { background-color: #111 !important; }
div#new-tweets-bar {
	color: #777 !important;
	background-color: #222 !important;
	border: none;
	text-shadow: none !important;
}
div.stream-item { border-bottom: 1px solid #111 !important;  }
div.stream-item.last-new-tweet { border-bottom: 1px dashed #777 !important; }
div.focused-stream-item {
	background-color: #000 !important;
	border: none;
}
span.tweet-user-name a { color: #999 !important; }
div.tweet-content { border-left: none !important; }
div.user-content-medium {
	background-color: #222 !important;
	-webkit-box-shadow: none !important;
}
.twttr-dialog { background-color: #333 !important; }
.twttr-dialog-header h3 { color: #777 !important; }
.twttr-dialog-inside { background-color: #222 !important; }
.thats-you { text-shadow: none !important; }

/* right hand */
div.dashboard span { color: #555 !important; }
div.dashboard div { color: #555 !important; }
div.pane-toolbar { background-color: #222 !important; }
div.pane-components { background-color: #111 !important; }
div.pane-components-inner { background-color: #111 !important; }
div.pane-components-inner div div.component { background-color: #111 !important; }
div.pane-components-inner div div.component div.clearfix { background-color: #222 !important; }
div.details-pane-tweet  { background-color: #111 !important; }
div.profile-actions { -webkit-box-shadow: none; }

}
