css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("habrahabr.ru") {

body {
    background: #e6e6e6 !important;
    font-size: medium !important;
}
#topline {
    height: 0 !important;
}
div#layout {
    max-width: none !important;
    padding: 0 10px !important;
    margin: 0 !important;
}
#header {
    line-height: 1.4em !important;
    margin: 10px 0 10px 0 !important;
    padding: 0.1em 0.5em !important;
    background: #f4f4f4 !important;
    border: 1px solid #999 !important;
    position: static !important;
}
div#header a.logo,
div.header a.logo,
div.comments_list h2.title,
div#header div.userpanel,
div.header div.userpanel,
div#footer,
div.content_left div.add_post,
.blog_header .blog-index {
    display:none !important;
}
div#header div.main_menu {
    font-size: medium !important;
    padding: 0 !important;
}
div#header div.search {
   position: static !important;
   float: right;
}
div.content_left table.menu * {
    background: #f4f4f4 !important;
    border: 0 !important;
}
div.content_left table.menu {
    background: #f4f4f4 !important;
    border: 1px solid #999 !important;
}
div.content_left div.submenu {
    background: #f4f4f4 !important;
    border: 1px solid #999 !important;
    border-top: 0 !important;
    border-radius: 0 !important;
    margin-top: -1px !important;
    margin-bottom: 10px !important;
}
div.post {
    background: #f4f4f4 !important;
    border: 1px solid #999 !important;
    padding: 0.25em 1em !important;
    margin-bottom: 10px !important;
}
div.content_left {
    width: auto !important;
    margin-right: 250px !important;
}
div.sidebar_right { 
    width: 230px !important;
    position: absolute !important;
    right: 10px !important;
}
.banner_240x400 {
    margin: 0 !important;
}
div.sidebar_right div.block {
    background: #f4f4f4 !important;
    border: 1px solid #999 !important;
    border-radius: 0 !important;
    margin-bottom: 10px !important;
}
div.comments_list {
    margin-top: 5px !important;
}
div.comments_list .comment .info {
   padding: 5px !important;
   background: #f4f4f4 !important;
   border: 1px solid #999 !important;
   border-bottom: 0 !important;
}
div.comments_list .comment .message {
   background: #f4f4f4 !important;
   border: 1px solid #999 !important;
   border-top: 0 !important;
   padding: 5px !important;
}
div.comments_list .comment .reply_comments {
   margin-top: 10px !important;
}
div.comments_list .comment .info .folding-dot {
   background: #999 !important;
   height: 1px !important;
}
div.comments_list .comment .info .folding-dot:after {
   content: "#";
}

}
