css:
@-moz-document domain("forum.tritiumnet.org") {

.status_main_content h4 a { color: #0A165B !important; font-size: 1em !important; }
#active_users a, #message_table a, #folders a, .topic_controls a, table.topic_list a, ul.topic_jump li a, .post_id a, .topic_controls, .category_block td a, .desc a, .category_block h4 a, td.col_c_post li a {color: #1A1A1A !important;} 
#ipboard_body { background: #111e2f !important;}
.maintitle, .general_box h3 {background: none !important; background-color: #7A8CBC !important;} 
.topic_prefix, .topic_prefix_opros, .topic_prefix_perem { color: #FFFFFF !important; float: none !important; display: inline-block !important; position: relative !important; bottom: 1px !important; margin-right: -7px !important; }
.topic_prefix { background-color: #D6644A !important; }
.citata { clear: none !important } 
.signature { margin: 10px 10px 0 6px !important; border-top: 1px solid #D6DDE4 !important; max-height: 100px !important; overflow: hidden !important; }
ul.post_controls { background: none repeat scroll 0 0 #DBDDE9 !important;} 
.post_block {  margin: 0 0 5px  !important; } 
tr.row2, tr.row1 { border-color: #E4DFD3 !important; border-style: solid none !important; border-width: 1px !important; }
.row2, .row1, .row1 td, .post_block.row1 { border-bottom: none !important; } 
#ipboard_body .input_rte { padding-left: 3px !important;} 
.message.unspecific { display: none !important; }
ol.subforums li a { font-weight:bold !important; color: #222222 !important; } 
div.ah30rep_label { display: none !important; } 
div.ah30rep_rep_image { float: left !important; } 
div.ah30rep_sidebar_wrap { margin-left: 5px !important; color: #222222 !important; padding-left: 0 !important; padding-right: 10px !important;} 
.hentry { clear: both !important; } 
li.row2 { background: none !important;} 
.status_main_content h4 { font-size: 1.1em  !important} 
.full_compose, .input_submit { font-size: 1em !important;  padding: 5px 9px !important;} 
#secondary_nav { font-weight: bold !important; } 
.tab_filters a { font-weight: bold !important;} 
.name a, #stats a, ul.ForumModeratorsLite a { color: #0A165B !important; } 
table.ipb_table { line-height: 120% !important; } 
.block_wrap { margin-bottom: 5px !important; } 
h5 { font-weight: normal !important;}
.status_mini_content a { color: #0A165B !important; font-weight: normal !important; } 
.ah30rep_sidebar_wrap .desc { color: #222222 !important; }
.__topic a { font-weight: normal !important; }
.post a, .entry-content a { color: #1A1A1A !important; } 
.post_block h3 a, .vcard a, h3>span.author.vcard>a.url.fn { color: #222222 !important; } 
.ip, .ip a { color: #505157 !important; } 
#post_options_options { display: inline !important }
#toggle_post_options { display: none !important }
td.short a { font-size: 13px !important }
tr.__topic a { font-size: 1.15em !important; }
td.altrow a, td.short a { font-size: 13px !important; }
.tab_filters ul text  { font-size: 14px !important; color: #222222 !important; }
.topic_controls { margin-top: 10px !important; } 
li.newposts { font-size: 0.95em !important; color: #373737 !important; }
.post_body .post { min-height: 130px !important; }
ul.last_post li strong { font-weight: normal !important; }
ul.last_post li a[href$="getnewpost"] { color: #373737 !important; font-weight: bold !important; }
ul.last_post li a[href*="showuser"] { text-decoration: underline !important; }
ul.last_post li a { font-size: 12px !important; color: #222222 !important; }
.status_main_content {  margin: 5px 0 !important; }
.status_main_wrap {  margin: 10px 0 0 !important; border-bottom: solid !important; border-color: #E4DFD3 !important; border-width: 1px !important; }
.warn_panel strong {color: #3E444A !important; font-size: 0.9em !important; }
address.author.vcard>a.url.fn { font-size: 1.1em !important; color: #0A165B !important; }
.general_box a { color: #0A165B !important; }
.hentry {color: black !important;}
td ._hovertrigger a { font-weight: normal !important; }
ul.last_post li a._hovertrigger { text-decoration: none !important; color: #373737 !important; font-weight: bold !important;  } 
img[src$="attachicon.gif"] { display: none !important }
caption.maintitle>span.desc a { color: #FFFFFF !important; }
div.blockquote { font-size: 0.95em !important; }
td.__topic>span.mini_rate { display: none !important }
span.author.vcard { min-width: 185px !important; display: inline-block !important; text-align: center !important; }
div.post_wrap>h3>img[src$="user_off.png"] { float: left !important; margin-top: 3px !important;  }
div.post_wrap>h3>img[src$="user_green.png"] { float: left !important; margin-top: 3px !important; }
p.posted_info { padding:   0px 5px 7px 7px !important; }
#search .choice { padding:   3px 9px 0 7px !important; }
div.post_body p.posted_info { color: #585E83 !important; }
table.ipb_table tr td { padding: 7px !important; }
tr.__topic>td>a[href$="getnewpost"], tr>td.__topic>a[href$="getnewpost"] { float: left !important }
span.topic_preview.right { display: none !important; }
br~div.topic_controls { margin: 0 !important; }
td>span.desc { font-size: 0.85em !important; }
span.topic_prefix_opros { margin-right: -7px !important; }
span.topic_prefix_perem { margin-right: -7px !important; }
tbody>tr>td>a { color: #0A165B !important; } 
ul#usercp_menu>li>a { color: #0A165B !important; } 
td.notify_title.desc { font-size: 0.85em !important; color: #222222 !important; }
span.desc.main_topic_desc { font-size: 0.9em !important; }
#stats dt, #stats dd { font-size: 0.9em !important; }
span.votes  { font-size: 0.9em !important; color: black !important; }
p.progress_bar>span { background:none repeat scroll 0 0 #4685B6 !important; }
div.post.entry-content { font: 14px/170% tahoma,verdana,tahoma,sans-serif !important; }
div.status_mini_photo { float: left !important; }
div.status_mini_content { margin-left: 37px !important; } 
ul.mini_pagination { font-size: 0.85em !important; } 
li.active { margin-top: -1px !important; }
ul#breadcrumb>li.first { margin-top: 2px !important; }
li.next a, li.prev a { margin-top: -3px !important; font-size: 1.2em !important; }
ul.pagination li.total { padding: 1px 4px 2px !important; }
span.nav_sep>text { font-size: 1.2em !important; } 
div.author_info li.mini_friend_toggle { display: none !important; }
div.status_feedback>div[style] { border-bottom: none !important; }
div#content-2>h2, p.message { margin-left: 10px !important; }
div#content-2>h2.maintitle { margin-left: 0 !important; }
div#topic_summary>div.post_block>h3>a:first-child { margin-left: 12px !important; }
form#modform>div.topic_controls, form#modform>h2.maintitle, form#modform>br { display: none !important; }
.col_f_topic, .col_m_subject { width: 45% !important; }
th.col_f_starter  { width: 14% !important; text-align: center !important; }
th.col_f_starter.short  { width: 10% !important;}
td.altrow { text-align: center !important; }
td.altrow.stats { text-align: right !important; }
div.post_form li.field>label { padding-top: 2px !important; }
div.poll_question { display: inline-block !important; clear: both !important; }
div.poll_question>ol { text-align: left !important; padding: 8px 8px 8px 0 !important; }
div.general_box.alt.poll { text-align: center !important; }
p.progress_bar.topic_poll { width: 100% !important; }
span.votes { font-weight: bold !important; float: right !important; }
div.general_box.alt.poll>form>span.desc { display: block !important; clear: both !important; left: 0 !important; }
.__topic a>img[src$="new_post.png"] { height: 0 !important; width: 0 !important; padding-left: 12px !important; padding-top: 12px !important; background: url(http://forum.tritiumnet.org/uploads/monthly_12_2011/post-2431-0-48026700-1322730593_thumb.gif) no-repeat !important; }
/* center>div#content.clear { display: none !important; } */
}
