@-moz-document domain("blog.polityka.pl") {


body{
margin:0px!important;
padding:0px!important;
background-color:#dddddd!important;
background-image:none;
}
.adtop, #adtop {
display:none;
}
.adrect {display:none!important;
}
.headerimage {display:none!important;
}
.mobileheaderimage {display:block!important;
}
#nav-below {text-align:center!important;
font:bold .8em Georgia,serif!important;
}

#page {margin-top:-20px!important;
width:845px;
padding-right:5px!important;
padding-left:15px!important;
}
#wrap {
}
.entry-details {width:1px!important;
display:none!important;
}

#main {width:540px!important;
}
#content {float:none!important;
width: 500px!important;
overflow:hidden!important;
padding:0px!important;
margin:0px!important;
}
#content .single-entry-subcolumn {float:none!important;
padding-left:20px!important;
text-align:justify!important;
margin:0px!important;
width:480px!important;
}
.p {padding-bottom:9px!important;
margin:0px!important;
}
.single-entry-subtitle {display:none!important;
}
.entry-inline-details {display:inline!important;
font:12px  Trebuchet MS,Arial!important;
font-weight:bold!important;
color:#999!important;
}
#content .entry-title {font: bold 20px Trebuchet MS,sans-serif!important;
text-align:left!important;
}


.more-link {
font:bold .9em Georgia,serif!important;
display:block!important;
padding-top:2px!important;
}
.entry-meta .commentscount{
font: bold 1.1em verdana,sans-serif!important;

}

#comments {width:500px!important;
	font-size: 12px;
	padding: 0 0 30px 0;
	border-top: 1px solid #e6e6e6;
}
#comments h3#comments-title {
	font: bold 1.2em Georgia,serif!important;
	text-transform:none!important;
	display:inline;
	margin-right: 20px;
}

#comments .comment-body p {
	font: 13px Verdana, sans-serif!important;
	padding:0 0 10px;
	color:#222!important;
	line-height:130%!important;
	text-align:justify!important;
	padding-right:15px!important;
}
p em, p i {font-style:italic!important;
font-family:inherit!important;
font-size:inherit!important;
}
.comment-body b, .comment-body strong {
	font-weight:bold;
}


#comments p.moderation {
	font-family:  Times, serif;
	font-style:italic;
}
#content #comments ol {
	padding:0;
}
#content #comments ol li ul.children {
	padding:0;
}
#content #comments ol li.depth-1 {
	padding:0 0 0 5%;
	border-bottom:1px solid #e6e6e6;
	overflow:hidden;
}

.comment-meta {
	padding:5px 0 10px!important;
	font-size: .8em!important;
}
.comment-meta cite.fn {
	font: bold 1.2em Trebuchet MS,Arial,Tahoma,Georgia,serif!important;
}


#content #comments #respond {
	margin: 10px 0;
	padding:50px 20px!important;
	background-color: #f7f7f7!important;
	font-size:.8em!important;
	font-weight:bold!important;
	color:#555!important;
}

#content #comments #respond h3#reply-title {
	margin:0 0 3px 0;
	font-size: 1.5em!important;
	font-weight: bold;
	font-family: Georgia,serif!important;
}

form#commentform input#author, form#commentform input#email, form#commentform input#url {
	width: 60%;
	padding: 2px 5px 2px;
	display: block;
	background-color:#fff;
	border: 1px solid #e6e6e6;
	font-size:1em;
}

#comments form input, #comments form textarea {margin-bottom:10px!important;
margin-top:2px!important;
font-size:1.3em!important;
}

#tertiary {width:220px!important;
overflow:hidden!important;
margin:0px!important;
background:#fcfcfc!important;
font-family:Trebuchet MS, Arial, sans-serif!important;
}
#tertiary ul {padding:0px!important;
margin-left:9px!important;
}
#tertiary h3, #tertiary h2 {margin-left:14px!important;
}
#tertiary h4 a {color:#931!important;

}
#rawhtmlembed-3 ul{font:12px Trebuchet MS, Arial,sans-serif!important;
display:inline!important;
padding:0px!important;
margin-left:3px!important;
color:#555!important;
}
#rawhtmlembed-4 a {color:#444!important;
}
#tertiary #archives-3 {text-align:center!important;
}
.recentcomments {font-weight:normal!important;
color:#444!important;
}
.search_photo a img {float:left!important;
padding:1px!important;
border:1px solid #ccc!important;
margin:0px 1px 2px 0px!important;
}
.search_content {padding:3px!important;
float:none!important;
padding-left:10px!important;
}
#search-3 {text-align:center!important;
}
#secondary {position:relative!important;
float:none!important;
clear:both!important;
width:100%!important;
}
#secondary .widget{margin-left:10px!important;
display:inline!important;
float:left!important;
position:relative!important;
margin-right:15px!important;
}
}

