css:
@-moz-document url-prefix(about:neterror) {
html {
    background:-moz-linear-gradient(
    center bottom,
    rgb(203,203,203) 25%,
    rgb(184,184,184) 75%
) !important;
    background-attachment:fixed !important;
    background-color:#333 !important;
    background-image: url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHAQMAAAD+nMWQAAAABlBMVEX///////9VfPVsAAAAAnRSTlMAGovxNEIAAAAdSURBVHheBcBBDQAABADAm3l4iiCKaKKbkEob6x4C7AD/ZNPMoQAAAABJRU5ErkJggg%3D%3D') !important;
    background-position:top !important;
    background-repeat: repeat !important;
}

*{font-family:"Segoe UI" !important;}

#errorPageContainer{
    padding:4em !important;
    margin:5em auto !important;
    background-origin: content-box !important;
    background-image: url('data:image/png;base64,') !important;
}
#errorTryAgain {
    -moz-margin-start: 0 !important;
    margin-top: 1em !important;
    width: 100% !important;
}
#errorShortDesc > p {
    border-bottom: 0 !important;
    padding-bottom: 0 !important;
}
h1 {
    border-bottom: 0 !important;
}
#errorTitle,#errorLongContent{
    -moz-margin-start: 120px !important;
}
}
