css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("facebook.com") {

/* Notifications */
#BeeperBox { bottom:50px !important }

/* Homepage Left Sidebar */
.ticker_stream .fbFeedTickerStory:nth-child(2n+2),
.navHeader {
background-color:#eef !important;
border-radius:5px 0 0 5px;
margin:0 !important;
padding:5px
}

.navHeader,
.navHeader a { color:#779 !important }

.item { border-radius:5px 0 0 5px; }

.uiSideNavCount {
background: -moz-linear-gradient(top,  rgba(241,231,103,1) 0%, rgba(254,182,69,1) 100%) !important;
border:1px solid rgba(254,182,69,1);
color:#fff !important;
text-shadow:1px 1px 0 rgba(0,0,0,0.5) }

/* Form */
.attachmentSelectForm { background-color:#eee !important }
.uiButton { border-radius:3px !important }

/* Profile Page */
.profile-friends { margin:7px 0 15px !important }
.profile-friends li {
background-color:#eef !important;
border-radius:5px 0 0 5px;
padding:3px;
}

.profile-friends li:nth-child(2n+2) { background-color:#ededef !important }

.uiPhotoThumb { border-radius:5px !important }
img { border-radius:3px }

.relationshipSection { padding:0 !important }
.uiHeader {
background-color:#3B5998 !important;
border-radius:5px 0 0 5px;
padding:3px 5px !important }

.uiHeaderTitle span,
.uiHeaderTitle,
.uiHeader a { color:#fff !important }

.uiHeader .fcg { color:rgba(255, 255, 255, 0.9) !important }

/* Comments */
.uiUfi .ufiItem { border-radius:5px !important }

}
