/* Bar Color */
#gbx3, #gbx4 {
    background: -moz-linear-gradient(
        top,
        rgb(255, 255, 255) 0%,
        rgb(255, 255, 255) 70%,
        rgb(250, 250, 250) 100%
    ) !important;
    background-color: #FFFFFF !important;
    border-bottom-color: rgb(239, 239, 239) !important;
    box-shadow: 0 1px 0 rgb(201, 215, 241) !important;}

/* Bar Item Hover Color */
.gbzt-hvr, .gbgt-hvr {
    background: -moz-linear-gradient(
        top,
        rgb(239, 243, 251) 0%,
        rgb(239, 243, 251) 70%,
        rgb(226, 230, 238) 100%
    ) !important;}

/* Bar Active Item Color */
.gbt .gbz0l .gbtb2 {
    border-top-color: rgb(26, 84, 225) !important;}

/* Text Color */
#mngb, .gbts {
    color: rgb(51, 102, 204) !important;}

/* Active Text Color */
.gbz0l .gbts {
    color: rgb(54, 54, 54) !important;}

/* Settings Splitter */
.gbts {
    background-position: 0 -22px !important;}

/* Settings Icon */
#gbi5 {
    background-position: -6px -22px !important;}

/* -----MOST of the MODIFICATIONS by DECKTRIO----- */

/* Uniform Blue Google Search */
#ab_name, .msel, .kls, .tbos, .tbot, .tbotu, .sel {
    color: #3366CC !important;}
.goog-list .goog-option-selected, .doclist-sorted-header {
    color: #3366CC !important;}

/* App Logos */
#logo-section, #logo-link, .kd-appname-wrapper, .kd-appname, .product-logo a {
    color: #3366CC !important;}

/* Google Reader NavColumn */
#nav .selected a, #nav .selected a .text {
    color: #3366CC !important;}
.scroll-tree li a.tree-link-selected .name, .scroll-tree li a.tree-link-selected:hover .name {
    color: #3366CC !important;}
.lhn-section-secondary li.folder.tree-selected a.tree-link-selected, .lhn-section-primary.tree-link-selected {
    border-left: 3px solid #3366CC !important;}

/* Google Apps Buttons */
.jfk-button-primary {
    background: #3366CC !important;}

/* Other Buttons */
.g-button-red {
    background-color: #3366CC !important;
    background-image: -moz-linear-gradient(center top , #3366CC, #3366CC) !important;}

.kls, #overview-selector.selected {
    border-left: 5px solid #3366CC !important;}

/* Google Shopping Start Page */
.GH3H4YHCJE h2 {color: #3366CC !important}
.GH3H4YHCKE h2 {color: #000000 !important}

/* Exceptions: Google Flights */
.GO14Y3UINB .GO14Y3UMNB {color:#3366CC !important;}
.GO14Y3UJOB {color:#3366CC !important}

/* New Google Menu */
.gbts {
    background: #FFFFFF !important;
    /* border-color: #F5F5F5 !important; */}

/* GMail */
