css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("xfce.org") {

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, footer, div, span, #titles, input.required, select.required, span.required_explanation

{
color: white !important;
background: black !important;
}

a

{
color: #0040ff !important;
}

#brdmenu a, #brdmenu a:link, #brdmenu a:visited

{
background: black !important;
}

/*highlight*/

.pun .blockmenu a:hover, .pun .blockmenu a:active, .pun .blockmenu a:focus, .pun .blockmenu .isactive a:link, .pun .blockmenu .isactive a:visited, #contentbody code

{
background: #333333 !important;
}

}
