css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("go.com"),
domain("abcnews.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, div#container, .footer, .footercontainer, .bodycontainer, .widgetsColumnAB, .mainWidgets, .freeform .midcontainer, .widgetsColumnA .freeform .ff_wrapper, div#siteSubNav div div, .showbg, .headerbg-new, #sectionnav #siteSubNav, .widgetsColumnA .wNode-1 .widget_head, span.text, .mainsection, .rightsection, div.title, .slideshow-widget .midcontainer, div.bubble, #rsslisting .midcontainer, #socialtoolkit .container, #socialtoolkit .midcontainer, div.core_container, #show_promo, div.widget_head, #byLineBox, .marketplace, .ab_cont, .c_cont, #s4a, div.midcontainer, #a_col_gray, #top5, div.subhead-title, .widget_head_3, .comment-mid, div.ptcPostedByCaption

{
color: white !important;
background: black !important;
border: black !important;
}

select, button, div.window, div.share_left

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
