css:
@-moz-document domain("harvard.indiebound.com") {

/* GENERAL */

a {
	text-decoration: none;
	color: black;
} 

a:hover {
	color: #ED2939;
	text-decoration: none;
}

h2 {
	font-family: 'Times New Roman',Times,serif;
	font-size: 24px;
	line-height: 28px;
}

/* SHELVES */

.abaproduct-details, .aba-search-add-to-cart-wrapper { margin-left: 10px; }

#aba-search-results-table { background: transparent url('http://harvard.indiebound.com/sites/harvard.indiebound.com/themes/skin/Shelf_RepeatingTop165.jpg') top left repeat-y; }

table #aba-search-results-table tr td:first-child { width: 165px; }

.abaproduct-image {
	height: 155px;
	width: 165px;
	padding-top: 10px;
	margin-top: 10px;
}

.abaproduct-title {
	margin-top: 10px;
	padding-top: 10px;
}

.abaproduct-image img {
	-moz-box-shadow: 0px 0px 3px 2px #888;
	-webkit-box-shadow: 0px 0px 3px 2px #888;
	box-shadow: 0px 0px 3px 2px #888;
}

.abaproduct-authors {
	font-size: 12px;
}

.abaproduct-other-editions {
	font-style: italic;
	font-size: 11px;
	margin-top: 5px;
}

.aba-search-result-table-spacer {
	padding: 8px 0 8px 0;
	margin: 10px 0 16px 0;
	background: url('http://harvard.indiebound.com/sites/harvard.indiebound.com/themes/skin/shelves_bottom_indie.jpg') top left repeat-x; 
}

.aba-search-result-table-spacer hr {
	display: none;
	width: 100%
}

/* HIDE THINGS */

.logo { display: none; }
#navbar { height: 45px; }

.breadcrumb { display: none; }

/* FORMS */

#edit-gbook-search-keys {
	width: 95%;
	border: 1px solid #BBB;
	padding: none;
}

#edit-search-block-form-1 {
width: 95%;
}

#edit-submit, #edit-submit-1, #edit-submit-2 { 
	background: #FFFFFF;
	text-transform: uppercase;
	text-decoration: none; 
	height: 30px; }

#edit-submit-1 a, #edit-submit-2 a { 
	color: #ED2939 }

#edit-keys {
height: 25px; 
border: 1px solid #BBB; }

#edit-keys-wrapper { 
margin-top: 5px; height: 25px; 
padding: none;
}

/* ADD TO CART BUTTONS */

.add-to-cart form input[type=submit]{
background: url('http://harvard.indiebound.com/sites/harvard.indiebound.com/themes/skin/AddToCart.png');
color: transparent;
font-size: 0;
width: 107px;
height: 32px; 
border: none; }

.add-to-cart form input[type=submit]:hover {
background: url('http://harvard.indiebound.com/sites/harvard.indiebound.com/themes/skin/AddToCart_hover.png');}

.abaproduct-cart {
padding: 10px;
margin: 5px;
}
.abaproduct-status {
padding-top: 5px;
}

/* SIDEBAR HEADINGS */

#block-menu-secondary-links h2, #block-uc_cart-0 h2, #block-gbook-0 h2, #block-search-0 h2, #block-block-3 h2, #block-block-4 h2, #block-menu-menu-shoppingcartextras h2, #block-block-5 h2, #block-block-6 h2 {
	background: #A6A6A6 url('http://harvard.indiebound.com/sites/harvard.indiebound.com/themes/skin/sidebar_title_bg.jpg') no-repeat top left;
	padding: 15px 10px 7px 10px;
	font-family: 'Times New Roman',Times,serif;
	font-size: 24px;
	font-weight: normal;
	line-height: 26px; 
color: #fff;}

#block-search-0 h2, #block-menu-secondary-links h2, #block-block-3 h2, #block-block-4 h2, #block-menu-menu-shoppingcartextras h2, #block-block-5 h2, #block-block-6 h2 {
	padding-top: 9px;
	background-position:0 -6px; }

#block-block-4 .content { text-align: justify; }

.sidebar div.content { line-height: 16px; padding: 4px;}

/* IN THE CART */

.desc { font-weight: bold; }

.messages.status {
padding: 10px;
Font: Arial, Helvetica, sans-serif 12px;
}

.sticky-enabled.sticky-table td {
padding: 10px;
}

.tableHeader-processed th {
padding: 4px;
}

/* THIS DOESN'T WORK -- shadow does not conform to size of jacket image :

.image img {
-moz-box-shadow: 0px 0px 3px 2px #888;
-webkit-box-shadow: 0px 0px 3px 2px #888;
box-shadow: 0px 0px 3px 2px #888;
}

*/

/* INDIVIDUAL BOOK PAGE */

.product-body p {
line-height: 18px;
padding: 15px 15px 15px 15px;
font: Arial, Helvetica, sans-serif 12px;
background: whitesmoke;
}

.clear-block.clear .meta, .clear-block.clear .links { display: none; }

}
