css:
@namespace url(http://www.w3.org/1999/xhtml); @-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/') 
 {
/*---------- Theme created by Rachel from pretaportre.tumblr.com ----------*/


/*---------- Background ----------*/
body {background: url('http://i43.tinypic.com/mrsniw.jpg') center right #A20308 repeat fixed !important;}
*{text-shadow:none !important;}
/*---------- Background End ----------/*

/*---------- General Content ----------*/
#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background-color:#A20308 !important; 
	opacity: .95 !important;}
#nav .nav_item.active
	{background-color:#A20308}
.permalink 
	{background: none !important;
	width: 16px !important;
	height: 16px !important;
  	background: #A20308) !important;
  	border-bottom-left-radius: 10px !important;
  	border-top-right-radius: 10px !important;
  	margin-top: 3px !important;
  	margin-right: 4px !important;}
h1,h2,h3,h4,h5,h6
	{color:#fff !important;}
a, a:visited
	{color:#408982 !important;}
a:hover
	{color:#ffffff !important;}
/*---------- General Content End ----------*/

/*---------- Nipple ----------*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) 
	{background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 11px white !important;
	border-right: 11px solid transparent !important;
	border-left: 11px solid transparent !important; }
.selection_nipple:not([class="selection_nipple white"]) 
	{border-bottom: solid 11px #A20308 !important;}
.notification, .avatar_frame, .nipple{border:0px !important}
/*---------- Nipple End ----------*/

/*---------- Dashboard ----------*/
#dashboard_switch_blog_menu_current,
#dashboard_switch_blog_arrow_and_divider,
#dashboard_switch_blog_arrow_current
	{background-image:none !important;}
#new_post_notice_container
	{color:#408982 !important;}
#new_post_notice_container a
	{color:#408982 !important;}
ol#posts li.notification
	{background-color: #A20308 !important;
	border-bottom: 2px solid #fff !important;
	color: #000000 !important;}
/*---------- Dashboard End ----------*/

/*---------- Right Column Highlight ----------*/
.notification_type_icon{display:none !important}
.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue, 
.selected .likes 
	{color: #FFFFFF !important; 
	background: #408982 !important;}
/*---------- Right Column Highlight End ----------*/

/*---------- Right Column ----------*/
a.explore_more_tags,
a.add_and_remove,
a.customize,
a.mass_editor
	{background-color:#A20308 !important;  
	background-image:none !important;
	border:0px !important;}
div.gradient,
div.dashboard_nav_item, 
a.permalink
	{background-color:#A20308 !important;
	opacity:.9; 
	background-image:none !important;
	border:0px !important;}
.content.is_media, 
.mask.top, 
.mask.bottom 
	{background: none !important; }
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{border-radius: 16px !important;}
.post.photo_post .photo, 
.post.photo_post .video_thumbnail 
	{-moz-box-shadow: 1px 1px 3px #000000 !important;}
/*---------- Right Column End----------*/

/*---------- Back to the Top ----------*/
#auto_pagination_loader
	{background: #FFFFFF !important;}
div.return_to_top_icon{	background: none !important;
	width: 0px !important;
	height: 0px !important;
	margin-top: 0px !important;
	border-bottom: solid 30px white !important;
	border-right: 15px solid transparent !important;
	border-left: 15px solid transparent !important;}

/*---------- Back to the Top End ----------*/

/*---------- Left Column ----------*/
#left_column #posts .post .permalink
	{background-color:#A20308;}
#left_column #posts .notification 
	{background-color:#A20308 !important;
	color:#B85858 !important;
	border-bottom-color: #eee !important;}
#left_column #posts .post .post_avatar
	{-moz-border-radius: 38px !important; 
	-webkit-border-radius: 60px !important; 
	width: 64px !important; height: 64px !important; 
	margin: 0px !important;}
#left_column #posts .post .sub_avatar_inner
	{-moz-border-radius: 38px !important; 
	-webkit-border-radius: 60px !important; 
	width: 32px !important; height: 32px !important; 
	margin: 0px !important;}
/*---------- Left Column End ----------*/


/*---------- Navigation Icons ----------*/
.new_post_label_icon{background-image:none !important;}
#new_post{background: url('http://i798.photobucket.com/albums/yy266/nautiical/tumblr/e03d284f.png') center no-repeat; background-color:none !important;}
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
    width:0;
    height:0px;
    opacity: 0;
    display: block;}
.new_post_label{
    font-size:0px !important;
    padding-top:0px !important;
    opacity: 0;}
/*---------- Navigation Icons End ----------*/


/*---------- Logo ----------*/
#logo 
	{height: 0 !important;
	width: 0 !important;
	padding-left: 327px !important;
	padding-top: 70px !important;
	background: url(http://i46.tinypic.com/x6bk9k.gif) no-repeat !important;}
/*---------- Logo End ----------*/}
