css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

/*
// Created by Decktrio
// on Decemeber 01, 2011
// I just want the light UI back. Also, I don't want social buttons.
*/

/* Hide Google Plus and Facebook and other unnecessary buttons */
#guide-reminders ul li:nth-child(1) {display:none !important;}
/* The above will work ONLY if you don't have Google Plus, otherwise delete or comment the line above. */
#social-guide-item {display:none !important;}
.yt-uix-expander-collapsed .guide-item-container.hideable {display:block !important;}
.guide-show-more-less {display:none !important;}
/* END OF SECTION HIDING SOCIAL BUTTONS */

/* Whitening the Background */
html, body {
    background: #FFFFFF !important;
    background-image: none !important;}
#masthead-container, #footer-container {background: #FFFFFF !important;}
#page.watch-branded #watch-sidebar, #page.watch-branded #watch-main-container {
    background: #FFFFFF !important;}

/* Whitening the Guide */
.guide, #channel {
    background-color: #FFFFFF !important;
    border-bottom: 1px solid #FFFFFF !important;}
.guide-background {
    background: #FFFFFF !important;}
.guide-item {
    background: #FFFFFF !important;
    color: #292929 !important;}
.guide-item-container {
    border-bottom: 1px solid #FFFFFF !important;
    border-top: 1px solid #FFFFFF !important;}
.guide-item.selected, .guide-item.selected:hover {
    color: #292929 !important;
    background-color: #F1F1F1 !important;
    border-right-color: #3366CC !important;}
#page h3.guide-item-container.selected-child, #page h3.guide-item-container:hover {
    border-top: 1px solid #FFFFFF !important;}
#guide-reminders ul {
    background: #ffffff !important;}

/* Lightening Headers */
#feed .feed-header {
    background: -moz-linear-gradient(center top , #F1F1F1 0pt, #F1F1F1 100%) repeat scroll 0 0 #F1F1F1 !important;
    color: #000000 !important;}
#guide-builder-promo {
    background: none repeat scroll 0 0 #F1F1F1 !important;}
#subscription-manager-header {
    color: #212121 !important;
    background-color: #F1F1F1 !important;
    background-image: none !important;}

/* Lightening Exceptions */
#subscription-manager {
    background: #FFFFFF !important;}
#subscription-manager .subscription-item:hover {
    background: #F1F1F1 !important;
    color: #212121 !important;}
#subscription-manager .subscription-item.odd {
    background: #F1F1F1 !important;}


/* END OF THEME */

}
