css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('youtube.com') {

#logo {

background: url('http://i.imgur.com/SadHz5R.png') no-repeat !important;

 }

.feed-item-owner, .feed-item-actions-line {

color: #666666 !important;

}


.video-list-item .title{

color: #1c62b9 !important;

}

.watch-view-count strong {

font-family: Arial !important;
font-weight: normal !important;
font-style:  !important;
font-size: 210% !important;

}

.caption-line-text {

color: #666666 !important;
}

.feedmodule-smtitle-wrapper, #homepage-main-content .feedmodule-smtitle, #homepage-main-content, #homepage-main-content .top-videos-module .video-list-item .title {

font-size: 12px;
font-weight: normal;
color: #333;

}

#masthead-nav a, #masthead-user a {

color: #1c62b9 !important;

}

#watch-action-confirmation #watch-action-response-message {

color: #000000 !important

}

.masthead-user-username {

color: #000000 !important;

}

#watch-description {

color: #000000 !important;

}

#watch-action-confirmation #watch-action-response-message {

color: #000000 !important;

}

.ytg-old-clearfix .ytg-box, .ytg-fl, .caption-line-text {

color: #000000 !important;

}




.watch-playlists-drawer, #watch-actions-area {

background: #c0c0c0 !important;

}

#watch-actions-loading {

color: #000000 !important;

}

.watch-playlists-drawer .playlist-title {

color: #000000 !important;

}

.playlist-public-private {

color: #000000 !important;

}

.created-at {

color: #000000 !important;

}

#masthead-container {
background: no-repeat url(http://i.imgur.com/oMBF1.png) 0 0 !important;
background-color: #ffffff !important;
}

body {

background: #ffffff !important;
color: #ffffff !important; no-repeat;
background-position: right bottom;
background-attachment:fixed;

} 

#ppv-container {

visibility: hidden !important;
position: absolute !important;

}

.comment {

color: #000000 !important;

}

#watch-owner-container label, .floatL, .watch-info-inline-edit label, .inline-edit-more h1, h2, h3, h4, h5, h6, .eow-threed-question {

color:#000000 !important;

}

.upload-drag-drop-description {

color: #000000 !important;

}

.informational-footer span {

color: #000000 !important;

}

.social-connector .info .social-network-name, .social-connector .info .social-network-username {

color: #000000 !important;

}

}
