css:
@-moz-document domain('yey.nu/'), url-prefix('http://yey.nu/') 

{

body {
    background: #fff !important;
    border-top: 50px solid #bb2727 !important;
	font-family:Helvetica, Arial, sans-serif !important;
    font-size: 15px !important;
    margin: 0;
}

::-moz-selection {
        background: #df2828;
        color: #fff;
}

#center {
    margin: auto;
    padding-top: 200px;
    width: 800px;
}
#upload {
    font-family:Helvetica, Arial, sans-serif !important;
    font-size: 12px !important;
    font-weight: bold;
    text-transform:uppercase;
    margin: 0;
    text-align: center;
}

.nodo {
width:100% !important;
}

#upload_box {
    background-color: #9e1616 !important;
    background-image: -moz-linear-gradient(#e43f3f, #790000) !important;
    border-radius: 7px 7px 7px 7px;
    color: #fff;
    margin: auto;
    padding: 6px !important;
    padding-left:0 !important;
    padding-right:0 !important;
    width: 315px !important;
    letter-spacing:-2px;
    text-transform:uppercase;
}

p {
padding:0px !important;
margin:0 !important;
margin-bottom:-5px !important;
margin-top:30px !important;
font-size:11px !Important;
text-align:right !Important;
}

div#upload_box:hover {
    font-size: 25px !important;
    opacity: 1 !important;
    background-image: -moz-linear-gradient(#e35454, #922121) !important;
}

div#upload_box:active {
    font-size: 25px !important;
    opacity: 1 !important;
    background-image: -moz-linear-gradient(#c32d2d, #5e0c0c) !important;
}

#menu {
    font-size: 16px !important;
    text-align:center !important;
    margin: 6px;
}
#menu_right {
    margin-top:5px !important;
    text-align:center !Important;
    font-size:16px !important;
    float:none !important;
}
.spacer {
    padding: 20px;
}
.copyright {
    height: 18px;
    position: relative;
    text-align: center;
    width: 100%;
}
#center i {
    font-size: 9px;
    text-align: center;
}
#menu_opt {
    background: none repeat scroll 0 0 #EEEEEE;
    border: 0 none;
    border-radius: 7px 7px 7px 7px;
    box-shadow: 0 1px 6px #000000;
    display: none;
    font-size: 14px;
    left:50%;
    margin-left: -137px !important;
    margin-top: -25px !important;
    position: absolute;
    width: 110px;
    z-index: 100;
}
#menu_opt ul {
    list-style: none outside none;
    margin: 0;
}
#menu_opt li {
    margin-left: -40px;
}
a, a:visited, a:active {
    color: #c72f2f !important;
    text-decoration: none;
}
a:hover {
    text-decoration: none !important;
    color:#f36e6e !important;
}



.qq-upload-button {
    font-family: arial,sans-serif;
    font-size: 48px;
    font-weight: bold;
    margin: 0;
    padding: 10px;
    text-align: center;
    text-shadow: -1px -1px 0 #383838;
}
.qq-upload-drop-area {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.4);
    border: 1px dotted #000000;
    color: #FFFFFF;
    height: 100%;
    left: 0;
    min-height: 380px;
    position: fixed;
    text-align: center;
    top: 0;
    width: 100%;
    z-index: 110;
}
.qq-upload-drop-area span {
    display: block;
    font-size: 16px;
    margin-top: -8px;
    position: absolute;
    top: 50%;
    width: 100%;
}
.qq-upload-drop-area-active {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7);
}
.qq-upload-list {
    display: none;
    list-style: disc outside none;
    margin: 15px 35px;
    padding: 0;
}
.qq-upload-list li {
    font-size: 12px;
    line-height: 15px;
    margin: 0;
    padding: 0;
}
.qq-upload-file, .qq-upload-spinner, .qq-upload-size, .qq-upload-cancel, .qq-upload-failed-text {
    margin-right: 7px;
}
.qq-upload-size, .qq-upload-cancel {
    font-size: 11px;
}
.qq-upload-failed-text {
    display: none;
}
#myfiles {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.4) !important;
    border: 1px solid rgba(0, 0, 0, 0.1) !important;
    -moz-border-radius:7px !important;
    box-shadow: 0 1px 6px rgba(0, 0, 0, 0.5) !important;
    color: #FFFFFF;
    margin: auto;
    float:left !important;
    min-height:20px !important;
    margin-left:15px !important;
    width:350px !important;
}
ul#files {
    list-style: none outside none;
    margin-left: -40px;
}

.box a, a:visited, a:active {
    color: #c72f2f !important;
    text-decoration: none;
}
.box a:hover {
    text-decoration: none !important;
    color:#fff !important;
    
}

.box {
    background: none repeat scroll 0 0 #ccc !important;
    border: 1px solid rgba(255, 255, 255, 1) !important;
    -moz-border-radius:7px !Important;
    box-shadow: 0 1px 6px rgba(0, 0, 0, 0.6);
    color: #FFFFFF;
    margin: 40px auto auto;
    min-height: 30px;
    padding: 12px;
    width: 500px;
}


.box a, .viewer a, #myfiles a {
    color: #CCCCCC;
}
#makemegodown {
    padding-top: 60px;
}
.viewer {
    color: #FFFFFF;
    margin: auto;
    width: 840px;
}
.box_left {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7);
    border: 2px solid rgba(0, 0, 0, 0.1);
    border-radius: 10px 10px 10px 10px;
    box-shadow: 0 1px 6px rgba(0, 0, 0, 0.6);
    color: #FFFFFF;
    float: left;
    margin-top: 20px;
    min-height: 30px;
    padding: 12px;
    width: 540px;
}
.box_right {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7);
    border: 2px solid rgba(0, 0, 0, 0.1);
    border-radius: 10px 10px 10px 10px;
    box-shadow: 0 1px 6px rgba(0, 0, 0, 0.6);
    color: #FFFFFF;
    float: right;
    margin-top: 20px;
    min-height: 30px;
    padding: 12px;
    width: 240px;
}
.box_comments {
    background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7);
    border: 2px solid rgba(0, 0, 0, 0.1);
    border-radius: 10px 10px 10px 10px;
    box-shadow: 0 1px 6px rgba(0, 0, 0, 0.6);
    color: #FFFFFF;
    margin: 100px auto auto;
    min-height: 30px;
    padding: 12px;
    width: 620px;
}
#bot_text {
    bottom: 0;
    font-family: arial,sans-serif;
    font-size: 12px;
    left: 0;
    margin: 6px;
    position: fixed;
}
.warning {
    background: none repeat scroll 0 0 #FB7A7A;
    border-bottom: 1px solid #EC0E0E;
    border-top: 1px solid #EC0E0E;
    color: #FFFFFF;
    font-size: 16px;
    padding: 2px;
    text-align: center;
    width: 100%;
}
input[name="login_user_big"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 14px;
    height: 25px;
    padding: 4px 6px;
    width: 225px;
}
input[name="login_pass_big"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 14px;
    height: 25px;
    padding: 4px 6px;
    width: 225px;
}
input[name="username"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 11px;
    padding: 4px 6px;
    width: 130px;
}
input[name="username_reg"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 14px;
    height: 25px;
    padding: 4px 6px;
    width: 225px;
}
input[name="email_reg"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 14px;
    height: 25px;
    padding: 4px 6px;
    width: 225px;
}
input[name="pass_reg"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 14px;
    height: 25px;
    padding: 4px 6px;
    width: 225px;
}
input[name="pass2_reg"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 14px;
    height: 25px;
    padding: 4px 6px;
    width: 225px;
}
input[name="url_to_file"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 12px;
    height: 15px;
    padding: 4px 6px;
    width: 185px;
}
input[name="password"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 11px;
    padding: 4px 6px;
    width: 130px;
}
input[name="submit"] {
    background-color: #FFFFFF;
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 16px;
    padding: 4px 6px;
}
input[name="control_code"] {
    border: 1px solid #CCCCCC;
    color: #000000;
    font-size: 14px;
    height: 25px;
    padding: 4px 6px;
    width: 10px;
}
#facebox {
    left: 0;
    position: absolute;
    text-align: left;
    top: 0;
    z-index: 100;
}
#facebox .popup {
    border: 3px solid transparent;
    border-radius: 5px 5px 5px 5px;
    box-shadow: 0 0 18px rgba(0, 0, 0, 0.4);
    position: relative;
}
#facebox .content {
    background: none repeat scroll 0 0 #FFFFFF;
    border-radius: 4px 4px 4px 4px;
    display: table;
    padding: 10px;
    width: 370px;
}
#facebox .content > p:first-child {
    margin-top: 0;
}
#facebox .content > p:last-child {
    margin-bottom: 0;
}
#facebox .close {
    background: none repeat scroll 0 0 #FFFFFF;
    padding: 2px;
    position: absolute;
    right: 5px;
    top: 5px;
}
#facebox .close img {
    opacity: 0.3;
}
#facebox .close:hover img {
    opacity: 1;
}
#facebox .loading {
    text-align: center;
}
#facebox .image {
    text-align: center;
}
#facebox img {
    border: 0 none;
    margin: 0;
}
#facebox_overlay {
    height: 100%;
    left: 0;
    position: fixed;
    top: 0;
    width: 100%;
}
.facebox_hide {
    z-index: -100;
}
.facebox_overlayBG {
    background-color: #000000;
    z-index: 99;
}
.hide_me {
    display: none;
}
tr.file_table:hover {
    opacity: 0.4;
}
td.fix_table {
    color: #000000;
    position: relative;
}
#info_upload {
    text-align: center;
    width:400px !important;
    background:#eeeeee !important;
    position:absolute !important;
    left:50%;
    margin-left:-210px;
    margin-top:15px; 
    color:#5e5e5e;
    -moz-border-radius:7px;
    border-radius:7px;
	border:solid #fecaca 1px;
    padding:10px;
}
img.showimage {
    max-height: 100%;
    max-width: 100%;
    min-height: 40%;
    min-width: 40%;
}
.real_link {
    float: right;
    text-align: left;
    width: 210px;
}
input[name="text"] {
    border: 1px solid #CCCCCC;
    border-radius: 7px 7px 7px 7px;
    box-shadow: 0 0 3px #FFFFFF;
    color: #000000;
    font-size: 16px;
    height: 40px;
    padding: 5px;
    width: 230px;
}
input[name="remote_url"] {
    border: 1px solid #CCCCCC;
    border-radius: 7px 7px 7px 7px;
    box-shadow: 0 0 3px #FFFFFF;
    color: #000000;
    font-size: 16px;
    height: 40px;
    padding: 5px;
    width: 230px;
}
.success {
    background: none repeat scroll 0 0 #0080FF;
    border-bottom: 1px solid #CCCCCC;
    color: #FFFFFF;
    font-size: 24px;
    font-weight: bold;
    height: 35px;
    left: 0;
    position: absolute;
    text-align: center;
    top: 0;
    width: 100%;
}
.fail {
    background: none repeat scroll 0 0 #FB7A7A;
    border-bottom: 1px solid #CCCCCC;
    color: #FFFFFF;
    font-size: 24px;
    font-weight: bold;
    height: 35px;
    left: 0;
    position: absolute;
    text-align: center;
    top: 0;
    width: 100%;
}
.fail2 {
    background: none repeat scroll 0 0 #FB7A7A;
    border-bottom: 1px solid #CCCCCC;
    color: #FFFFFF;
    font-size: 24px;
    font-weight: bold;
    height: 35px;
    left: 0;
    position: absolute;
    text-align: center;
    top: 0;
    width: 100%;
}
#info_upload {
    font-size: 10px;
}
}
