css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com'), url-prefix('https://www.tumblr.com')   {

/*sets text color for ALL text*/
* {  color:#695D51 !important;}

/*BIG BACKGROUND begin - background for the entire screen*/
body {
  background:url("http://28.media.tumblr.com/tumblr_lvmp9lgh3V1qbb2wpo1_400.jpg")!important;
  background-color: #fde9c9 !important;
  background-position: right bottom!important;
  background-repeat:no-repeat !important;
  background-attachment: fixed !important;
}

/*BIG BACKGROUND end*/

/*LOGO begin - tumblr logo*/
#logo {
    height: 0 !important;
    width: 0 !important;
    padding-left: 287px !important;
    padding-top: 89px !important;
    background: url(http://29.media.tumblr.com/tumblr_lvmkx6LHOF1qbb2wpo1_250.jpg) no-repeat !important;}
/*LOGO end*/

/*ICON BAR begin - new post icons bar - graphic replacement*/
img[src*="http://assets.tumblr.com/images/new_post_icons_no_labels.png"]{
opacity: 0 !important;
height:70px;  
}
#new_post{
  background-image: url(http://28.media.tumblr.com/tumblr_lvmkwul6oU1qbb2wpo1_500.png) !important;
  background-position: center center !important; 
  background-repeat: no-repeat !important;
}

ol#posts .post.new_post .new_post_label {
display:inherit !important;}
/*ICON BAR  end*/

/*ICON BAR BACKGROUND begin - background for the icon bar*/

#new_post {
  background-color: rgba(255, 255, 255, 1.0) !important;
}
/*ICON BAR BACKGROUND end*/

/*AVATAR ARROW begin - arrow to the left of the icon bar*/
#new_post .arrow {
  opacity: 1.0 !important;
  margin-left: 0px !important; 
}
/*AVATAR ARROW end*/



/*CONTENT begin - background for posts, icon bar, sidebar*/

#content_top, #content_bottom {
visibility: hidden !important;
background: rgba(105,93,81,0.4) !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
  background: rgba(185,163,141,0.3) !important;
}

/*CONTENT*/ 



/*POST BACKGROUND begin*/

#posts LI[class*=" post"] {

  background: rgba(255, 255, 255, 1.0) !important;

}

/*POST BACKGROUND end*/



/*SELECTION ARROW begin - points to dashboard selection*/

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(185,163,141,0.3) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}



.selection_nipple:not([class="selection_nipple white"]) {

  border-bottom: solid 11px rgba(185,163,141,0.3)  !important;

}

/*SELECTION ARROW end*/



/*GREEN BUTTONS begin - blue/green buttons from sidebar to transparent*/

li.selected, li.recessed, a.active, div.post_filter_tabs a {

  background: none !important;

}

/*GREEN BUTTONS end*/



/*RADAR begin - removes radar blue frame - softens appearance*/

.post.photo_post .photo, .post.photo_post .video_thumbnail {

  -moz-box-shadow: 1px 1px 3px rgba(0, 0, 0, 0.0) !important;

  border-radius: 12px !important;

}

.content.is_media, .mask.top, .mask.bottom {

  background: none !important;

}

/*RADAR end*/



/*NEW CONTENT begin - text post message body background*/

.mceContentBody {

  color: rgba(0, 0, 0, 1.0) !important;

  background: rgba(255,255,255, 1.0) !important;

}

#nav .nav_item.active,

#nav .nav_item .nav_menu,

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

  background-color:#fde9c9 !important;

  background-image:none !important;

  border:0px !important

}

#posts .avatar_frame, .notification.single_notification {

  border: 1px rgba(105,93,81, 1.0) solid !important;

}

#left_column #posts .post .post_avatar{-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 64px !important; height: 64px !important; margin: 0px !important;}



#left_column #posts .post .sub_avatar_inner {



-moz-border-radius: 38px !important; -webkit-border-radius: 60px !important; width: 32px !important; height: 32px !important; margin: 0px !important;}



#left_column #posts .notification {

background-color: #B9A38D !important;

color: #695D51 !important;

border-color: #695D51 !important

}

#left_column #posts .notification.alt {

background-color: #fde9c9 !important;

border-color: #695D51 !important

}


#left_column #posts .notification a {

color: #695D51 !important;

border-color:#695D51 !important;

}

#left_column #posts .notification blockquote {

border-color: #695D51 !important;

color: #695D51 !important;

}
}
