css:
@-moz-document domain("school6.ya.dn.ua") {
body{
    background: rgba(0, 0, 0, 1) !important;
    background-image: url(http://2.bp.blogspot.com/-Rmb395UPA88/T-jJNuFus6I/AAAAAAAACCI/FLS2v9O3JbA/s1600/seamless_dark_decorative_background.jpg) !important;
}

body > div > img{
    display: none;
}

body > div {
    height: 50px !important;
}

body > div::before {
    content:"Ясиноватская школа ОШ №6";
    text-align: center;
    color:white;
    text-shadow: 0px 0px 5px black;
    font-size:40px;
}



h3{
  color:white !important;
  text-shadow:0px 0px 20px #1425F0;
}
#time{
  color: white;
  text-shadow:0px 0px 5px black;
}
table{width:100%;color:white;}

#leftpanel, .menu, #menu_top, #menu_bottom, #menu, #about, #menu div{
    background: transparent !important;
    /** color: black !important; /**/
   /* font-size:10px;*/
}
#title{
  text-shadow: black 4px 4px 8px, red 0 0 1em;
}

.r1, .r2, .r3, .r4, .r5, .lwhite-bg, .ltr_style {
    display: none !important;
}

div.inner-box, div.inner-boxa {
    border-radius: 10px !important;
    border: solid 1px #00F !important;
    background: rgba(209, 233, 255, 0.4) !important;
}

h3.ads, #favorites h3 {
    background: none !important;
    text-indent: 40px !important;
}

td, tr b, .white-bg, .tr_style {
    border: none !important;
    background: none !important;
}

.tr_style td {
    padding-top:7px;
}
.tr_style table {
    display: none;
}

.tr_style div table {
    display:block;
}

.menu_subsection {
    border: solid #AAF;
    border-width: 0px 1px 1px 0px;
    width: 110px;
    padding: 2px 0px 2px 25px;
    position: relative;
    top: 0px;
}

.menu_section {
    border-style: double;
    border-color: #AAF;
    border-width: 1px 1px 1px 0px;
    border-radius: 0px 10px 10px 0px;
    margin-top: 2px;
    padding: 2px 0px 2px 15px;
    position: relative;
}

.menu_section a {
    color: white;
    /** padding-left: 15px; /* */
}

.menu_section a:hover {
    color: #88F;
    /** padding-left: 15px; /**/
    text-shadow: 0px 0px 5px red;
}

.menu_subsection a {
    color: #AAF;
}
.menu_subsection a:hover {
    color: #88F;
    /** padding-left: 15px; /**/
    text-shadow: 0px 0px 5px red;
}
#guestform{
  text-align:center;
}
#guestform label{
  float:left;
  width:50%
}
#valcode{
  margin-left:50%;
}
#guestform textarea{
  width:100% !important;
}
#guestform #submit{
  height:2em !important;
  border-radius:5px;
  border:1px #00F solid !important;
  background: rgb(209, 233, 255) !important;
  color:black !important;
  padding:0.2em;
  box-shadow:2px 2px 5px black;
}
}
