css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.sancity.ru") {

#mainframe
{width: 95% !important;}
a:link, a:visited
{color: #ff8040 !important;}
a:hover
{color: #aa00aa !important;
text-decoration: none !important;}
body
{background: #202020 !important;}
blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote
{	color: #808080 !important;
	border: 1px solid #505050 !important;
	margin: 1px;
	padding: 1px;
	font-size: x-small;
	line-height: 1.4em;
	overflow: auto;}
/* Alterate block quote stylings */
blockquote.bbc_standard_quote
{	background-color: #303030 !important;}
.titlebg, .titlebg2, tr.titlebg td, tr.titlebg2 td
{	color: #bbb !important;
	background: -moz-linear-gradient(#300030, #202020, #202020, #202020, #300030) !important;}
.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th
{	color: #ddd !important;
	background: -moz-linear-gradient(#300030, #202020, #202020, #202020, #300030) !important;}
.windowbg, #preview_body, .content, .roundframe
{	color: #808080 !important;
	background: -moz-linear-gradient(#300030, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #300030) !important;}
.windowbg2
{	color: #808080 !important;
	background: -moz-linear-gradient(#300030, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #202020, #300030) !important;}
.windowbg3
{	color: #808080 !important;
	background-color: #303030 !important;}
ul.admin_menu li ul li a
{	display: block;
	padding: 0.5em 2em 0.5em 0.5em;
	font-size: 90%;
	text-decoration: none;
	background: none;
	color: #ff8040 !important;}
.buttonlist ul
{background: #202020 !important;}
.buttonlist ul li a span
{	background: #202020 !important;}
.buttonlist_bottom ul
{background: #202020 !important;}
.buttonlist_bottom ul li a span
{background: #202020 !important;}
.buttonlist_bottom ul li.active a span em
{background: #202020 !important;}
.buttonlist_bottom ul li.active a
{background: #202020 !important;}
.main_menu ul
{	background: #202020 !important;}
.main_menu li a span
{	background: #202020 !important;}
.main_menu li.last a span
{	background: #202020 !important;}
.main_menu li.active a span em
{color: #fff !important;
background: #202020 !important;}
.main_menu li.active a span
{	background: #202020 !important;}
.main_menu li.active a
{	background: #202020 !important;}
h3.titlebg, h4.titlebg
{	border-left: 1px solid #500050 !important;
	border-right: 1px solid #500050 !important;}
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td
{	border-bottom: 1px solid #500050 !important;
	border-top: 1px solid #500050 !important;}
.catbg, tr.catbg td, .catbg3, tr.catbg3 td
{	background: -moz-linear-gradient(#300030, #202020, #202020, #202020, #300030) !important;}
.infocenter_section div.sectionbody
{	border-left: 1px solid #505050 !important;
	height: auto !important;}
.left_admmenu, .left_admmenu ul, .left_admmenu li
#left_admsection
{	background-color: #202020 !important;
	padding: 1px;
	border: 1px solid #500050 !important;
	width: 160px;
	margin-right: 10px;}
.bordercolor
{	background-color: #505050 !important;}
.tborder
{	padding: 2px !important;
	border: 1px solid #600060 !important;
	background-color: #000 !important;}
/* Container for the new admin menu */
#adm_container
{background: #202020 !important;}
ul.admin_menu
{background: #202020 !important;}
/* First layer of menu items */
ul.admin_menu li
{background: #202020 !important;}
ul.admin_menu li.chosen h4
{background: #202020 !important;
color: #fff !important;}
ul.admin_menu li h4
{	margin: 0;
	padding: 7px 5px 3px 5px;
	cursor: pointer;
	font-weight: normal;
	font-size: x-small;
	text-transform: uppercase;
	color: #ff8040 !important;}
h1, h2, h3, h4, h5, h6
{color: #fff !important;}
div#menu_toggle
{background: #202020 !important;}


}
