css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.stumbleupon.com")  {

/* StumbleUpon - Dark Shiny Blue, transparency   */
/* based on http://userstyles.org/styles/11922 and using same background image */

body {
	background: url("http://i6.photobucket.com/albums/y249/uglook/tech3.jpg") #222 fixed repeat left top !important;
	color:#fff !important;
}

body .listStumble .views span, body .colRight .listUrls .views span, body a, body #dashboard a,
.audienceTools #content p.subhead, .audienceTools p, .widgets_landing #content p.subhead, .widgets_landing ul.featureBoxes p {
	color: #6AABD3 !important;
}

/* Backgrounds */
.l-shadow-left:after, .l-shadow-right:after {
    background: none !important;
}
.wrapper-content, .home-logged-in .stumbles,
.home-logged-in .l-content-primary header {
	background: none !important;
}
.home-logged-in .l-content-primary header {
    border-bottom: 2px solid rgba(100,150,200,0.5) !important;
}
footer {
	background-color: rgba(0,0,0,0.5) !important;
}
#l-nav-primary a {
    background: rgba(50,75,100,0.75) !important;
}
#l-nav-primary .s-active a, #l-nav-primary .s-active a:hover {
	text-shadow: 1px 1px 2px #000000;
	box-shadow: 0 0 3px 3px #99CCFF, 0 -2px 5px 0 rgba(0, 0, 0, 0.75) inset !important;
}
.l-master-a .l-column-right-content {
	background: rgba(51,82,126,0.5) !important;
}
.nav-side li a {
    background: rgba(100,100,100,0.12) !important;
}
.nav-side {
	border-top: 15px solid rgba(0,0,0,0.1) !important;
}
.l-content-primary,
.nav-side .s-active a, .nav-side .s-active a:hover,
.nav-side li a:hover {
    background: rgba(6,55,105,0.5) !important;
}
.nav-side .s-active a {
	text-shadow: 1px 1px 2px #000000;
	box-shadow: 0 0 4px 4px #99CCFF, 0 -2px 5px 0 rgba(0, 0, 0, 0.75) inset !important;
}
.stumble-item, .pagination {
    background-color: rgba(10,30,51,0.5) !important;
    border-color: rgba(10,30,51,0.5) !important;
}
.s-connections-grid .stumble-item:hover {
    background-color: inherit !important;
}
.pagination a, .pagination span {
    background-color: rgba(20,61,102,0.5) !important;
    border-color: rgba(255,255,255,0.2) !important;
}
.pagination .page-active {
    background-color: rgba(30,91,153,0.5) !important;
    border-color: transparent !important;
}
.pagination .page-disabled {
    background-color: transparent !important;
}

pre, .table-alt tbody tr:nth-child(2n+1) td {
    background-color: rgba(245, 245, 245, 0.5) !important;
}

/* Other stuff */
.btn.primary {
	color: #000 !important;
	text-shadow: none !important;
}

p.intro, .wrapper-comment p {
	color: #CCC !important;
}





/* OLDER STUFF */

h1, h2, h3, h4, h5, h6 {
	color: #89A8B6 !important;
}

/* Text-shadows */
a, .UIActionButton_Text, span, div, input[value="Comment"] {
	text-shadow: #000 1px 1px 1px !important;
}
a:hover, input[type="submit"]:not(.fg_action_hide):hover, a span:hover, a:hover *, button:hover {
	color: #99ccff !important;
	text-shadow: #fff 0px 0px 2px !important;
	text-decoration: none !important;
}
/* end text-shadow section */


}
