css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rawstory.com") {

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, .wrapper, #mid-slider, div#mainmenu, div.wrappershadow, #secondarycontent .colleft_mid

{
color: white !important;
background: black !important;
}

select, button, div.dsq-comment-header

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd

{
color: white !important;
}

/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, input, code

{
background: #333333 !important;
}

}
