@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {

body {
background: url("http://savepic.su/2804433.jpg")
no-repeat fixed center !important;
font: 85% Verdana, Helvetica, arial, sans-serif !important;
}

#main-header {
background-color: #4DA2FA !important;}

H1 {
color: #FFD8B7!important;
font: 170% Verdana, Helvetica, sans-serif !important;}

#main-header > H1 > A {
color: #FFFFFF !important;}

#left-sidebar > NAV {
background-color: #FFD8B7 !important;}

#search {
background-color: #FFD8B7 !important;}

#related {
background-color: #FFD8B7 !important;}

#left-sidebar > NAV > UL > LI > A {
color: #4DA2FA !important;
font-weight: bold;}

#left-sidebar > NAV > UL > LI {
color: #FFFFFF !important;
font-size: 14px !important;
font-weight: bold;}

#show-search-options {
color: #4DA2FA !important;
font-size: 12px !important;
font-weight: bold;}

#search-options {
color: #4DA2FA !important;
font-size: 10px !important;
font-weight: bold;}

#related > H2 {
color: #4DA2FA !important;
font-size: 12px !important;}

#related > UL > LI > A {
color: #4DA2FA !important;
font-size: 10px !important;
font-weight: bold;}

.more {
color: #4DA2FA !important;
font-size: 12px !important;
font-weight: bold;}

#miscellaneous-info > TBODY > TR:first-child > TD > A {
color: #4DA2FA !important;
font-weight: bold;}

A[rel="nofollow"][href="/contact"] {
color: #4DA2FA !important;
margin-left: 7px !important;}

#install {
background-color: #FFD8B7 !important;}

#front-page-best > H2, #front-page-newest > H2, #more-info > H2,
#discussions-area > H2, #discussions-area > H3, #main-article > H2,
#main-article > TABLE > THEAD > TR > TH:nth-child(2),
#main-article > TABLE > THEAD > TR > TH:nth-child(3),
#main-article > TABLE > THEAD > TR > TH:nth-child(4),
#main-article > TABLE > THEAD > TR > TH:nth-child(5),
#main-article > TABLE > THEAD > TR > TH:last-child,
#main-article > DL > DT,
#main-article > FORM > TABLE > TBODY > TR:first-child > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(2) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:last-child > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(3) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(4) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(5) > TH > LABEL,
#main-article > FORM > TABLE > TBODY > TR:nth-child(8) > TH > LABEL,
#edit-style-options > THEAD > TR > TH:first-child,
#edit-style-options > THEAD > TR > TH:nth-child(2),
#edit-style-options > THEAD > TR > TH:last-child,
#main-article > FORM > TABLE > TBODY > TR:nth-child(6) > TH,
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TH,
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:first-child,
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:nth-child(2),
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:nth-child(3),
#main-article > FORM > TABLE > TBODY > TR:nth-child(7) > TD > TABLE > THEAD > TR > TH:last-child
{color: #4DA2FA !important;}

#overlay {
width: 100% !important;
}

#main-article > UL,#front-page-best > UL,#front-page-newest > UL {
color: #4DA2FA !important;}

#view-code {
background-color: #DDEEF6 !important;
}

#screenshots {
background-color: #FFFFFF !important;
}

textarea {
min-height: 150px !important;
font: 95% Verdana, Helvetica, arial, sans-serif !important;
}
#style_long_description:focus {
min-height: 230px !important;
}
#style_additional_info:focus {
min-height: 230px !important;
}
#css:focus {
min-height: 600px !important;
}

input[name="style[screenshot_url_override]"] {
width: -moz-calc(100% - 22em);
}

.form-controls td > dl > dd > img {
margin: 10px 0 5px !important;
}

.form-controls td > dl > dd > input[type="checkbox"] {
margin: 0 0 6px -21px;
}
}
