css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("blogs.mail.ru") {

/* for modern style */
#bloger_grad, #left_block_content>td.center>div.center_pattern {
  display: block !important;
}

#left_block_content>td.center>div, #left_block_content>td.center>div>div  {
  display: none;
}

/* for classic style */
table.upr, #gogo, td[width="23%"]>div, div.mb10, table.upr0 {
  display: none;
}

/* better layout, can be buggy */
table.mt10 {
  margin-top: 150px !important;
  width: 80% !important;
}

table.lelt_nav, table.lelt_nav_star {
  position: relative;
  top: -140px;
  width: 700px !important;
}

table.lelt_nav>tbody>tr, table.lelt_nav_star>tbody>tr {
  display: table-cell;
  float: left;
}

table.lelt_nav td.com a, table.lelt_nav_star td.com a {
  white-space: nowrap;
}

div.px0 {
  width: 0 !important;
}

td.left_column>div {
  width: 0 !important;
}

}
