css:
@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {
	
}


#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #dashboard_followers, #dashboard_members, body{



background-image:url('http://i42.tinypic.com/30rlh93.png') !important;



background-repeat: no-repeat !important;



background-attachment: fixed;



background-color:#bdbdbd !important;



background-position: left bottom !important



}















#dashboard_index div.l-content, #dashboard_submissions div.l-content, #dashboard_drafts div.l-content, #dashboard_post_queue div.l-content, #dashboard_members div.l-content, div.l-content #inbox{



background: rgba(0,0,0,0.7) !important;



}







#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: rgba(0,0,0,0.7) !important;}







#dashboard_edit_post{



background-color: #bdbdbd !important



}















#right_column .dashboard_nav_item {



border-bottom:0px solid #ad2626 !important;



background-image:none  !important







}















#right_column .dashboard_nav_item .dashboard_nav_title,



                    #right_column .dashboard_nav_item .dashboard_nav_title a,



                    #right_column .dashboard_nav_item a,



                    #right_column .dashboard_nav_item ul.dashboard_subpages,



                    #right_column .dashboard_nav_item ul.dashboard_subpages li a {



                        color:#ad2626 !important;







}















#right_column .dashboard_nav_item ul.dashboard_subpages li a.tracked_tag_control {



                        color:#ddbda6 !important ;



                    }















#right_column .dashboard_nav_item #dashboard_controls_radar_media_fold {



                        background: none !important;



                    }







#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#000 !important;







                    }















#left_column #posts .notification .avatar:hover {opacity:1 !important;}







.post{opacity:1 !important; border-bottom:0px !important; color:#000; font-size:11px; font-family:Century Gothic;}



.post_title {font-family:Lucida Handwriting !important; font-size:16px !important; color:#eee;}



.post:hover{opacity:1 !important}



.post_info, .post_controls, .nav_item, a, a:active, a:visited {color:#7f7f7f !important; text-decoration:none;}



.post_info {color:#000!important;}



.permalink{background-color: #ad2626 !important; background-image:none !important}



.active{background-color:#f9ccc9 !important; color:#96a59a !important;}



.nav_item_nipple{display:none !important}



.nav_menu{background-color:#ad2626 !important; border-bottom:0px !important; color:#96a59a !important;}











#nav .nav_item.active,



#nav .nav_item .nav_menu,



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {



background-color:#ad2626 !important;



background-image:none !important;



border:0px !important;



color:#7d8a81 !important;



}















#nav .nav_item .nav_menu {



border-bottom:0px !important



}















#nav .nav_item .nav_menu a {



color:#c18d6a !important;



border-top-color:#322115 !important;



}







#nav .nav_item .nav_menu a:hover {



color:#7d8a81 !important;}











.dashboard_switch_blog_menu_header{



background-color:#17130d !important;



color:#777 !important;



}







.dashboard_switch_blog_menu_item{



background-color:#ad2626 !important;



color:#777 !important;



border:0px !important



}







.dashboard_switch_blog_menu_footer{



background-color:#ad2626!important;



color:#ad2626 !important;



}







.dashboard_switch_blog_menu_wrapper{



background-color:#ad2626!important;



}















#new_post_notice_container{



color:#ad2626!important



}















#new_post_notice_container a{



color:#fff !important



}















#dashboard_switch_blog_menu_current,



#dashboard_switch_blog_arrow_and_divider,



#dashboard_switch_blog_arrow_current



{background-image:none !important}











#footer{



background: #ad2626!important;



background-image:none !important



}

#left_column #posts .notification {



                        background-color:#727070!important;



                        color:#eee !important;



                        border-bottom-color: #000 !important



                    }















#left_column #posts .notification .block {



                            color:#727070!important;



                        }















#left_column #posts .notification.alt {



                        background-color: #727070!important;



border-bottom-color: #ad2626!important



                    }







#left_column #posts .notification.last_notification,



                    #left_column #posts .notification.single_notification {



                        border-bottom-color:#ad2626 !important;



                    }







#left_column #posts .notification a {



                        color:#eee!important;



                        border-bottom:0px !important;



                    }







#left_column #posts .notification blockquote {



                        border-left-color:#eee !important;



                    }















#left_column #posts .notification .avatar {



                        border-color:transparent !important;



                        background-color:#ad2626!important;



                    }















#left_column #posts .notification .avatar:hover{



                    }



.notification_type_icon{display:none !important}











h1,h2,h3,h4,h5,h6{color:#efa061 !important}




