@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.youtube.com/watch"),
url-prefix("https://www.youtube.com/watch") {

@media all and (max-width:1293px) {
/*Player*/
#player-api, div.player-api.player-width.player-height
{width: 920px !important;
height: 575px !important;
margin-left:60px !important;
}
/*Underplayer Stuff*/
#watch7-content {margin-top: 10px !important;}
#watch7-sidebar {padding-top: 20px !important; margin-top: 0 !important;}
}
    
@media all and (min-width:1294px) {
/*Player*/
#player-api, div.player-api.player-width.player-height
{width: 920px !important;
height: 575px !important;
margin-left:160px !important;
}
/*Underplayer Stuff*/
#watch7-content {margin-top: 10px !important;}
#watch7-sidebar {padding-top: 20px !important; margin-top: 120px !important;}
}
    
/*Unstuck Top Panel only for watch pages*/
#masthead-positioner {position:absolute !important;}

 }



@-moz-document domain("youtube.com") {

/*Unstuck Top Panel everywhere;
 buggy on YT channels*/
/*#masthead-positioner {position:relative !important;}
#masthead-positioner-height-offset {height:0 !important;}*/
    
div.yt-masthead-logo-container {width:auto !important;}
    
/*Guide Button*/
button#appbar-guide-button {background-color:transparent !important; border:0 !important;}
button#appbar-guide-button img.yt-uix-button-arrow {display:none !important;}

/*Browse Upload | Sign In*/
#masthead-upload-button-group, #yt-masthead-signin, #upload-btn {display:none !important;}
#yt-masthead-content, #masthead-search {max-width:80vw/*920px*/ !important;}


/*Comments*/
#watch-discussion {display:none !important;}

/*YT Notifications*/
/*#alerts, [class="yt-alert-content"],*/
div.yt-alert.yt-alert-default.yt-alert-info, div.yt-alert.yt-alert-default.yt-alert-warn {display: none !important;}

/*YT Tickers*/
#ticker {display: none !important;}

/*Views Count, Likes,Dislikes*/
#watch7-views-info, button#watch-like, button#watch-dislike {visibility: hidden !important;}

/*Buttons Height*/
#content-container button, #footer-container button,
#watch-more-related-button
{min-height:22px !important; max-height:22px !important;}


/*Colors*/
#content {background-color: #333 !important;}
body, #yt-masthead-container, #footer-container, #masthead-appbar-container, #appbar-guide-menu, #appbar-settings-menu {background: #333 !important;}
#yt-masthead-container {border-bottom:none !important}
#watch-description-toggle, #watch-description-collapse {border-top:none !important}
p, h2, h3, h4, b, li, strong, #eow-title, [class="vfacets"], [class="grayText"], [class="watch-view-count"], [class="viewcount"],
[class="comments-section-description"], [class="stat"], [class="stat view-count"],[class="search-option"],  [class="search-option-label"],[class="video-alt-query"] {color: #3399ff !important;}
a, [class="title"] {color: #009900 !important;}
a, [class="title"], li {text-shadow:none !important;}
a:visited {color: #ff8000 !important;}
span.yt-badge {color:#3399ff !important; border-color:#3399ff !important;}
/*new*/
#player, #guide-container, #watch7-sidebar, #watch7-sidebar-contents {background: #333 !important;}
#masthead-appbar-container{border-bottom-color:#333 !important;}
#masthead-appbar {background: #333 !important;}
#c4-primary-header-contents, #channel-subheader {background: #333 !important;}
#c4-header-bg-container {border-bottom:0 !important;}
#appbar-guide-menu, #guide-container {border-right:1px solid #999 !important;}
img#logo, .footer-logo-icon{background-color:#666 !important;border-radius:7px !important;}

