css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("theulstermanreport.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h1, h2, h3, h4, h5, h6, p, form, footer, strong, div.top-section, div.sidebar, div.slider-wrapper-shade, div.two-third-width, .page

{
color: white !important;
background: black !important;
border: black !important;
}

.sidebar 
{
    box-shadow: 8px 0 26px #111111 inset !important;
}

/*links*/

a

{
color: #0040ff !important;
}

/*highlight*/

textarea, pre

{
background: #333333 !important;
}

}
