css:
/* készítette: adamika2000 */
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("prog.hu"),domain("pcforum.hu") {
body {
overflow-x:hidden;
}
.pagetop,.mainframe,.pageheader {
width:100% !important;
}
.inputformdefault {
width:80% !important;
}
#input textarea {
width:90%;
resize: vertical;
}
.langmenu td {
text-align:center;
}
.sharebar .list span {
text-align:center;
}
.CODEDIV {
max-height:400px;
overflow-y:auto;
margin:15px;
padding:5px;
background-color: #E1E1E8;
/* keret by A-Ty */
border: solid 1px #AAC3CB;
}
#accepted .CODEDIV {
background-color: #E8F0E8;
}
.msg_quote {
margin:15px;
margin-bottom:0px;
padding:5px;
background-color: #FFFFFF;
display:block;
}
.msg_quote:before {
display: inline;
content: '\201C';
}
.msg_quote:after { 
display: inline;
content: '\201D'; 
}
.previewbox {
padding-left:20px;
padding-right:20px;
}
input,textarea {
outline:none;
}
.commonsearch {
width:100% !important;
}
#frame .forumentry td[colspan="2"] a[rel="nofollow"] {
background-image: url("http://smslib.org/img/external.png");
background-position: 100% 50%;
background-repeat: no-repeat;
padding-right: 15px;
}
}
