css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
 * @title BugTracker.NET Metro UI Theme (Facebook Scheme)
 * @version 1.0
 * @lastUpdated 2013-06-07
 * @author thenonhacker
 */

@-moz-document url-prefix('http://YourBugTrackerUrlHere'), url-prefix('http://ifdefined.com/btnet/') {
  body {margin:0px!important;}
  body, a, input, textarea, p, .buglist_popup, td, select, .short_desc_static, .cmt_text, .err {font-family:Tahoma!important;}
  body, a, input, textarea, p, p.MsoNormal, .buglist_popup, .short_desc_static, .bugid, .cmt_text, .smallnote, .click_to_sort, .pst, .err {font-size:8pt!important;}
  a, input {color:#3B5998!important}
  input, textarea {padding:3px!important;}

  p.MsoNormal span font font font {mso-fareast-font: 8pt "Lucida Grande",Tahoma!important; font: 8pt Tahoma!important;}
  p.MsoNormal + p.MsoNormal { margin-top: 0.5em!important; } 

  .short_desc_static, .short_desc_input {padding:3px!important; border-color:#eee!important; margin-top:-4px!important;}

  div.align {margin-left:16px!important; margin-right:16px!important;}
  form#ctl00.frm {border-width:0px!important; background-color:#fff!important;}

  .buglist_popup{border:1px solid #ddd!important; padding:2px!important; background-color:#fff!important;-moz-box-shadow: 3px 3px 3px #888, 3px 3px 9px rgba(0,0,0,0.4)!important;}

  input.menubtn {background-color:#3B5998!important; border:1px solid!important; border-color: #5B79b8 #1B3978 #1B3978 #5B79b8 !important;}
  input.menubtn:active {background-color:#2B4988!important; border:1px solid!important; border-color: #1B3978 #3B5998 #3B5998 #1B3978 !important; outline: 0!important; padding: 4px 2px 3px 4px!important; margin-bottom:1px!important; }
  input.menubtn::-moz-focus-inner { border: 0; }

  input.btn {color:#fff!important; background-color:#3B5998!important; border:1px solid!important; border-color: #5B79b8 #1B3978 #1B3978 #5B79b8 !important; font-weight:bold!important;}
  input.btn:active {background-color:#2B4988!important; border:1px solid!important; border-color: #1B3978 #3B5998 #3B5998 #1B3978 !important; outline: 0!important; padding: 4px 2px 2px 4px!important;}
  input.btn::-moz-focus-inner { border: 0; }

  select {border:none!important;}
  select.filter {border:none!important;background:#fff!important}
  select.filter_selected {border:none!important;}

  a:hover {text-decoration:underline!important;}

  .bugt {border:none!important;}
  .bugh {font-weight:bold!important; background:#fff!important; border:none!important; text-align:left!important; text-transform: uppercase!important; vertical-align:bottom!important;}
  .bugh a {color:#333!important; font-size:7pt!important;line-height:8pt!important;}
  .bugf {background:#fff!important; padding-bottom:6px; border:none!important;border-bottom:3px solid #eee!important;}
  .bugd {border-left:none!important;border-right:none!important;border-bottom-color:#eee!important; }

  .logo {background-color:#3B5998!important; font-family:Klavika,"Segoe UI"!important; font-size:16.7pt!important; text-transform: lowercase!important; margin-left:16px!important;}

  .menubar {background-color:#3B5998!important; border:none!important;}
  .menubar a, .menubar .smallnote, input.menubtn {color:#eee!important; font-size:8pt!important; font-weight:bold!important; text-transform: capitalize!important;}

  #ctl00 {margin-left:16px!important; margin-right:16px!important;}
  .smallnote, .click_to_sort {color:#aaa!important;}

  table#posts_table {border-spacing:0!important;}
  .cmt {border-top:1px solid #fff!important; border-left:0!important; border-right:0!important; border-bottom:1px solid #d2e9e7!important; background-color:#edeff4!important;}
  td.cmt table td {padding:3px!important;}
  .pst {color:#999!important}
  .gray_link, .smallnote {font-size:8pt!important;}

  .datat {border:none!important;}
  .datah {font-weight:bold!important; background:#fff!important; border:none!important; text-align:left!important; text-transform: uppercase!important; vertical-align:bottom!important;}
  .datah a {color:#000!important; font-size:8pt!important;line-height:8pt!important;}
  .datad {border-left:none!important;border-right:none!important;border-bottom-color:#eee!important;}

  a.tab_btn, a.tab_btn_pushed {border-color:#ccc!important; font-weight:bold!important;  text-align:left!important; text-transform: uppercase!important; vertical-align:bottom!important;}
  a.tab_btn {border-width: 0px 0px 1px 0px !important;}
  a.tab_btn_pushed {border-width: 0px 0px 4px 0px !important;}

  .menubar {display:float!important;}
}
