css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://t.qq.com/"){
/*侧栏在左侧*/
.side{
position: absolute!important;
left: 0px!important;}
.navArr{margin-left:-198px!important;}
#mainWrapper{margin-left:auto!important;}
.main{margin:0 0 0 320px!important;}
#top-pic-list{padding-top:16px!important;}
/*移动按页码翻页链接*/
#pageSetLink{position:relative !important;
left:-100px !important;
top:0px !important;}
/*圆角、阴影 */
#mainWrapper{box-shadow:0 0 5px #666;}
.side{border-radius: 5px 0px 0 0 !important;}
.main{border-radius:0 5px 5px 0  !important;border-right:none!important;}
#talkBox{border-radius:0 5px !important;}
/*透明*/
/*
#mainWrapper{background-color:rgba(215,245,255,0.8)!important;}
.main{background-color:rgba(255,255,255,1)!important;}
*/
/*数据栏*/
.m_profile_h .c2  {width:320px!important;padding-left:1px!important;border-top:1px solid #E4E4E4 !important;}
.main{border-left:1px solid #E4E4E4!important;}
/*一些颜色*/
.m_profile_h .c2,
.m_profile_h .list,
.main,
#talkBox,
.insertFun 
{background:#F7F7F7!important;}
/*输入框三角形*/
.sendCnt .a_l{
right:auto!important;
left:-20px!important;
border-width: 0 10px 10px 10px!important;
border-color: transparent #D0D0D0 transparent transparent!important;
border-style:solid!important;
cursor:default!important;
}
.sendCnt .a_l u{
border-width: 0 8px 8px 8px!Important;
border-color:rgba(255,255,255,0) rgba(255,255,255,1) rgba(255,255,255,0) rgba(255,255,255,0) !important;
border-style:solid!important;
margin:1px 0 0 -6px!important;
cursor:default!important;
}
.talkBox .focus .a_l{
border-right-color:#7BD6F6 !important;
}
/*一点过滤*/
li[tv], /*微博推广*/
.new_sc_rmdfollow, /*推荐收听*/
#app_appUsed,#app_ad, /*最近使用*/
.topNavItem .ico_hot_mini /*顶栏小标*/
{display:none!important;}
}
