css:
@-moz-document domain(tumblr.com) {
body {background: #b3c6ca url('http://static.tumblr.com/8yo5xxv/7SFlvm3bf/1.png') fixed !important; background-position: top right!important; background-repeat: no-repeat!important;}

ol#posts li blockquote,



body.mceContentBody blockquote {



margin-left: 0 !important;



margin-right: 0 !important;



padding-left: 10px !important;



border-width: 4px !important;



border-color: #b3c6ca !important;



}



ol#posts li blockquote blockquote,



body.mceContentBody blockquote blockquote {



border-color: #b3c6ca !important;



}



ol#posts li blockquote blockquote blockquote,



body.mceContentBody blockquote blockquote blockquote {



border-color: #b3c6ca !important;



}



ol#posts li blockquote blockquote blockquote blockquote,



body.mceContentBody blockquote blockquote blockquote blockquote {



border-color: #b3c6ca !important;



}



ol#posts li blockquote blockquote blockquote blockquote blockquote,



body.mceContentBody blockquote blockquote blockquote blockquote blockquote {



border-color: #b3c6ca !important;



}



ol#posts li.notification.single_notification {

border-bottom: 1px solid #000000;

}



#dashboard_index #left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: rgba(224,238,238,0.5) !important;

  }



#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {

    background: rgba(224,238,238,0.5) !important;

    -webkit-border-radius: 20px;

    -moz-border-radius: 20px;

    border-radius: 20px;

    margin: auto;

    padding: 40px auto;

   padding-top: 15px!important; padding-bottom: 15px!important;  

  }


.no_posts_found, h1.dashboard_header {

color: #666666;

}



img#content_top, img#content_bottom {

display: none;

}



#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {

    background: none;

  }



#right_column .dashboard_nav_item {

    background: none;

    background-attachment: initial;

    background-position-x: 50%;

    background-position-y: 100%;

    background-origin: initial;

    background-clip: initial;

    background-color: initial;

}



#right_column .dashboard_nav_item #hide_radar {

background-image: none;

background-repeat-x: no-repeat;

background-repeat-y: repeat;

background-attachment: initial;

background-position-x: 0%;

background-position-y: 0%;

background-origin: initial;

background-clip: initial;

background-color: transparent;

}



#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {

background: rgba(224,238,238,0.5);

}



#nav .nav_item.active {

    background: rgba(224,238,238,0.5) !important;

  }



#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {

    background: rgba(224,238,238,0.5) !important;

  }






#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {

background: rgba(224,238,238,0.5);

border: solid #000000;

}



ol#posts li.post .post_avatar{

border-bottom: 0px solid #0A2939;

}



#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {

border-top: 1px solid #000000;

border-bottom: 1px solid #000000;

}



#right_column a#dashboard_switch_blog_menu_current:hover {

background: none;

}



#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {

background: none;

}



form.dashboard_options_form {

background-color: rgba(224,238,238,0.5);

border-bottom: 1px solid #000000;

border-bottom-left-radius: 10px 10px;

border-bottom-left-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-bottom-right-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-left-radius: 10px 10px;

border-top-right-radius: 10px 10px;

border-top-right-radius: 10px 10px;

color: #cccccc;

display: block;

font-size: 13px;

margin-bottom: 25px;

padding: 15px 22px;

}



form.dashboard_options_form .option_container {

border-top: 1px solid #000000;

}



.no_posts_found, h1.dashboard_header {

color: #333333;

}



.answer_container {

background-color: #000000;

border-bottom: 0px solid #24394E;

}



#nav .nav_item .nav_menu {

background-color: #000000;

border-bottom: 2px solid #333333;

}



#nav .nav_item .nav_menu a {

border-top: 1px solid #333333;

}



#auto_pagination_loader {

background: rgba(224,238,238,0.5) none repeat-x 50% 0%;

border-top: 0px solid #1B2D42;}

a{text-decoration: none;}

#pagination a{background-color: #E0EEEE; color: #a6b9ca; -webkit-border-radius: 4px; -moz-border-radius: 4px; border-radius: 4px;}

h1{color:#ffffff; margin:0 0 10px 0; padding:0; font-size:35px;line-height:24px;letter-spacing:-1px; text-shadow: none!important;}

div.l-content ::-webkit-input-placeholder{color:#ffffff;-webkit-font-smoothing:antialiased;}div.l-content :-moz-placeholder{color:#ffffff;}div.l-header ::-webkit-input-placeholder{color:#ffffff;-webkit-font-smoothing:antialiased}div.l-header :-moz-placeholder{color:#ffffff;}



#right_column .radar{-webkit-font-smoothing:antialiased;margin:15px 0 0 0}#right_column .radar .content{background:url('http://static.tumblr.com/8yo5xxv/LYylvwn1d/radar_sprite.png') repeat-y;position:relative}#right_column .radar .content.is_media{width:223px;background-position:-466px 0;margin:0 0 0 -4px}#right_column .radar .content.is_media .post{margin:0 0 0 4px}#right_column .radar .content.is_media .post img,#right_column .radar .content.is_media .post embed,#right_column .radar .content.is_media .post object,#right_column .radar .content.is_media .post iframe{width:215px}#right_column .radar .content.is_media .post .caption{display:none}#right_column .radar .content.is_media .play_arrow{width:46px;height:45px;background:url('http://static.tumblr.com/8yo5xxv/LYylvwn1d/radar_sprite.png') -1960px 0 no-repeat;margin:-27px 0 0 -23px;position:absolute;top:50%;left:50%}#right_column .radar .content.is_text{width:187px;color:#4e4e4e;font-size:13px;line-height:18px;background-position:-699px 0;margin:0 0 0 -4px;padding:13px 18px 26px}#right_column .radar .content.is_text .title,#right_column .radar .content.is_text .quote,#right_column .radar .content.is_text .link{font-size:14px;font-weight:bold}#right_column .radar .content.is_text .link a{color:#415c78}#right_column .radar .content.is_text p,#right_column .radar .content.is_text ol,#right_column .radar .content.is_text ul,#right_column .radar .content.is_text .title,#right_column .radar .content.is_text .quote,#right_column .radar .content.is_text .link{margin:0 0 6px 0}#right_column .radar .content.is_text *:last-child{margin-bottom:0}#right_column .radar .content.is_text .audio{margin-top:4px}#right_column .radar .content.is_text .audio *{border-bottom:1px solid #fff}#right_column .radar .content.is_text img{max-width:187px;border-bottom:1px solid #fff}#right_column .radar .content .mask{width:223px;background:url('http://static.tumblr.com/8yo5xxv/LYylvwn1d/radar_sprite.png') no-repeat;position:absolute;left:0}#right_column .radar .content.is_media .mask.top{height:9px;background-position:0 0;top:0}#right_column .radar .content.is_media .mask.bottom{height:19px;background-position:0 -9px;bottom:0}#right_column .radar .content.is_text .mask.top{height:10px;background-position:-233px 0;top:0}#right_column .radar .content.is_text .mask.bottom{height:20px;background-position:-233px -10px;bottom:0}#right_column .radar .content .like_and_reblog{width:48px;height:20px;position:absolute;top:9px;right:10px;overflow:hidden;display:none}#right_column .radar .content .like_and_reblog a{line-height:200px;background:url('http://static.tumblr.com/8yo5xxv/LYylvwn1d/radar_sprite.png');opacity:.9;overflow:hidden}#right_column .radar .content .like_and_reblog a:hover{opacity:1}#right_column .radar .content .like_and_reblog .reblog{width:24px;height:20px;background-position:-1864px 0;float:right}#right_column .radar .content .like_and_reblog .like{width:21px;height:20px;background-position:-1898px 0;margin:0 0 0 3px;float:right}#right_column .radar .content .like_and_reblog .like.red{background-position:-1929px 0}#right_column .radar:hover .content .like_and_reblog{display:block}#right_column .radar .attribution{height:35px;font-size:11px;margin:6px 0 0 29px}#right_column .radar .attribution img{-webkit-border-radius:3px;-moz-border-radius:3px;border-radius:3px;-webkit-box-shadow:0 1px 3px rgba(0,0,0,.58);-moz-box-shadow:0 1px 3px rgba(0,0,0,.58);box-shadow:0 1px 3px rgba(0,0,0,.58);margin:0 10px 0 0;float:left}#right_column .radar .attribution img:active{-webkit-box-shadow:0 0 2px rgba(0,0,0,.58);-moz-box-shadow:0 0 2px rgba(0,0,0,.58);box-shadow:0 0 2px rgba(0,0,0,.58);position:relative;top:1px;filter:alpha(opacity=90);-moz-opacity:.9;opacity:.9}#right_column .radar .attribution .username{max-width:143px;color:#b9c5d5;font-size:12px;font-weight:bold;text-decoration:none;display:inline-block;white-space:nowrap;overflow:hidden;text-overflow:ellipsis}#right_column .radar .attribution .label_and_follow{height:13px;color:#95a6bd;overflow:hidden}#right_column .radar .attribution .label_and_follow a{color:#95a6bd;text-decoration:underline}

#posts .post .permalink{width:23px;height:23px;background:url('http://static.tumblr.com/8yo5xxv/lKQlvwnfn/dogear.png');position:absolute;top:-4px;right:-5px;display:none}

/* Code repaired by admin. See http://forum.userstyles.org/discussion/37591/attempting-to-mass-fix-broken-tumblr-dash-icon-styles */
.new_post {
	background: #ffffff url('http://i46.tinypic.com/257oe84.jpg') center center no-repeat !important;
}
.new_post_label_icon {
	background-image: none !important;
}


}
