css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("about.com"),
domain("tqn.com")

{

/*text&background*/

html, body, tbody, thead, th, tr, td, blockquote, li, ul, h2, h3, h4, h5, h6, font, strong, p, form, footer, nav, div#main, #p1, #p2, #p3, .fn, #abw, .pane, #browse h3, #browse .h3, #browse .n3, #fpromo h3, #fpromo .h3, #fpromo .n3, .hl, #sspre h2, #sspre .h2, #sspre, #ssimg img, #abh, #pagination .tt, #aba, #widgets

{
color: white !important;
background: black !important;
}

select, button, #abt, #abm, #abf, .tr, .recipe

{
background: black !important;
}

div, span, a

{
background-color: black !important;
}

select, input, span, code, textarea, pre, label, div, dd, option

{
color: white !important;
}

/*links*/

a

{
color: #0040ff !important;
}

a:visited

{
color: #224375 !important;
}

/*highlight*/

textarea, pre, #up a, .gs input

{
background: #333333 !important;
}

}
