css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
url-prefix(http://),
url-prefix(https://),
url-prefix(file://),
url-prefix(ftp://){

html {
background-image:none !important;
background-color: #001717 !important;}

/* textarea etc */
html textarea,input {
background-color: #333 !important;
background-image:none !important;
-moz-appearance: none !important;
font-size: 100% !important; color: #ccc !important;
}

/* button */
button, 
input[type="reset"],
input[type="button"],
input[type="submit"] { 
  -moz-appearance: button !important;
  color: #333 !important;
}

html,pre,body,div,span:not(.mw-headline),table,td,p,font,li,ol,ul,dl,blockquote,form,b,big,br,label{
color: #bb9 !important;
font-size: 14pt !important;
font-family: arial !important;
line-height: 1.25em !important;
text-align: justify !important;
word-spacing: normal !important;
}

.s em {
color: #ddd !important;
}

h1,h2,h3,h4,h5,h6{
font-family: verdana !important;
/* font-weight: bold !important; */
color: #999 !important;
}


a:visited, a:visited>*, a:visited>*>* {color: #c33 !important;}
a:link, a:link>*, a:link>*>*, a, a>*, a>*>* {color: #66f !important;}
a:hover, a:hover>*, a:hover>*>* {color: #3c3 !important;}

nav,img,
a:not(.on), header,div,table,tbody,tr,td,p,font,blockquote,form, h1,h2,h3,h4,h5,h6,li,ol,ul,dl,b,span,body,pre,html>body>pre,
label{
background-image:none !important;
background-color: transparent !important;
}

a{
font-size: 14pt !important;
font-family: arial !important;
}
 

body{margin-left: 7% !important;
margin-right: 7% !important;
width: auto !important;}

small {
font-size: 11pt !important;
}

/* .gac_m,#gbi,#gfx-searchers-menu=google #searchInputSuggest=wikipedia 
div[id^="tOoLtIp"]=la #search_suggest=amazon
#trans2=google translate #yn-tooltip=yahoo news .yt-menulink-menu,.google-ac-m=youtube #dhtmltooltip,#popitmenu=ebookshares  .autocompleter-choices=tv.com #notes_text_div=flickr
.submenu=bdsmlibrary .nav_popover=amazon .PopUp=allrecipes.com
*/

/* auto-suggest */
 .gac_m, #searchInputSuggest, #search_suggest, .google-ac-m, .autocompleter-choices, [class*="suggest"], 
[id*="suggest"], [class*="autocomplete"], [id*="autocomplete"]{
background-image:none !important;
background-color: #006 !important;
}

/* ? */
#placeholder98767,button {
background-image:none !important;
background-color: #060 !important;
}

/* dropdown menu */
.yt-menulink-menu, #popitmenu, .submenu, #gbi, #gfx-searchers-menu, [class*="dropdown"], [id*="dropdown"] {
background-image:none !important;
background-color: #600 !important;
}

/* popup/tooltip */
.b-dialog,
div[id^="tOoLtIp"], #trans2, #yn-tooltip, #dhtmltooltip, #notes_text_div, [class*="popup"], [id*="popup"],
 .PopUp, [class*="tooltip"], [id*="tooltip"], .nav_popover, #visibleBalloonElement {
background-image:none !important;
background-color: #202 !important;
}
}
