css:
@-moz-document url-prefix("http://twitter.com"), url-prefix("https://twitter.com"), domain("twitter.com") {
.wrapper {
background: none;
}

.content-main .username {
font-size: 14px;
color: #333;
float: left;
}

.content-main .username b {
font-weight: bold;
}

.content-main .fullname {
font-weight: normal;
color: #999;
font-size: 12px;
margin-left: 5px;
}

.tweet .details {
font-size: 11px;
color: #aaa;
}

.original-tweet .actions i, .stream-item-footer .js-icon-container {
display: none;
}

.with-icn {
font-size: 11px;
}

.content-main {
float: left;
width: 535px;
}

.dashboard {
float: right;
width: 302px;
}

.module ul:last-child li:last-child .list-link, .module .flex-module:last-child {
-webkit-border-bottom-right-radius: 0;
-webkit-border-bottom-left-radius: 0;
-moz-border-radius-bottomright: 0;
-moz-border-radius-bottomleft: 0;
border-bottom-right-radius: 0;
border-bottom-left-radius: 0;
}

.module ul:first-child li:first-child .list-link, .module .flex-module:first-child {
-webkit-border-top-left-radius: 0;
-webkit-border-top-right-radius: 0;
-moz-border-radius-topleft: 0;
-moz-border-radius-topright: 0;
border-top-left-radius: 0;
border-top-right-radius: 0;
}

.dashboard .module {
margin-bottom: 0;
border: none;
}

.tweet-user {
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
}

.content-header .header-inner {
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
}

.content-header {
border-top: none;
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
}

.time, .time a, .metadata, .metadata a {
font-size: 10px;
}

.before-expanded, .after-expanded {
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
}

.open.stream-item {
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
margin: 0 !important;
}

.tweet .actions {
background: none;
}

.module .list-link {
padding: 8px 12px 8px 28px;
}

.module .chev-right {
left: 12px;
-moz-transform: scaleX(-1);
-o-transform: scaleX(-1);
-webkit-transform: scaleX(-1);
transform: scaleX(-1);
filter: FlipH;
-ms-filter: "FlipH";
}

.webkit .module .active .list-link {
padding-left: 28px;
}

.list-link .avatar-row, .list-link .media-row {
margin-left: -16px;
}

.profile-nav .component .component .list-link {
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
}

.stream-item.open:first-child {
-webkit-border-radius: 0;
-moz-border-radius: 0;
border-radius: 0;
}

.account, .interest-category, .list, .saved-search, .tweet, .app {
padding-bottom: 10px;
}

.last-new-tweet {
border-bottom: 1px black solid;
}

.dm-convo {
height: 100%;
}

.dm-dialog .twttr-dialog-inside {
height: 100%;
}

.dm-threads {
height: 100%;
}



[data-component-term="user_recommendations"] { display : none!important; }
}
