@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(facebook.com){
/*
* Facebook in Paris Night
*
* Author      : Yancie
* Created     : 11/12/11
* Version     : 2.2
* Update      : 20/12/11
* Description : Beautiful Paris Night design for facebook
*
*/

.ad-container, #rightCol,#profile-friends-footer,#fblSponsors,#BuzzboxMusicWidgetMock, #fbPhotoSnowboxAdsBottom, #fblPhotoSnowboxAd,#MessagingNetegoWrapper,.UIStandardFrame_SidebarAds,.emote_img
{visibility:hidden !important}

.ginormousProfileName,.attachmentName,.messageBody, .actorDescription, .content,.fcb, .uiHeaderPage .uiHeaderTitle, .fbEventHeadline, .fwb, .note_content, .UIImageBlock_Content .fwb, .translationEligibleUserMessage, .tlTxFe, .aboveUnitContent, .uiHeaderTitle .name
{color:#FFF !important}

.fbProfileByline a, #profile_minifeed a, .default_message,.uiLinkButton input, .uiBoxLightblue,.uiMorePagerSecondary, .linkWrap, .fbxWelcomeBoxName, .actorName a,.passiveName, .UIActionLinks_bottom a, .profileFriendsText a, .fcg a, .uiStreamPassive a, .uiHeaderSubTitle a, .photoText strong,.fbPhotoPublicLink a,.noCaption, .commentContent a, .UIImageBlock_Content a, #MessagingReadParticipants a, .friendListSuggestionNameWrap, .friendListMoreSuggestions, .mediaPageName, .data a, a, .snippet, .fbSettingsListItemEdit, .attachmentFrame .fwb, .blueItemLink, .like_link, .fbTimelineScrubber li, .attachmentName
{color:#FF99CC !important}

.uiHeaderTitle, .fbSettingsEditorLabel strong, .fbSettingsEmailMasterControlPanel .fwb, .uiBoxWhite .fwb, .fbSettingsNotifTable, .gdp_permission_title,.uiStreamHeaderText,#pagelet_ticker .uiStreamMessage .messageBody, #pagelet_ticker .attachmentLink .fwb,.pop_content .fwb
{color:#000 !important}

.fbFriendListMemberBoxTitle, #pagelet_friend_list_suggestions .uiHeader, .profile_stream_composer, .infoSectionHeader, .profileInfoSection .uiHeader, .uiBoxWhite, .uiHeader
{opacity:0.6 !important}

.fbTimelineStickyHeaderComposerContainer {opacity:0.95 !important}

.ministoryMessage, .uiHeaderSubTitle,.fbPhotoCaptionText,.fbPhotoPublicLink div, #pagelet_main_column_personal .data, .uiAttachmentDetails, .fbSettingsListItemLabel strong, .mvm, .UIImageBlock_Content, .gdp_wide
{color: gray !important}

.uiBoxLightblue,#pageFooter, #contentCurve, .MessagingReadHeader,#followOptinPrompt, .content p, .aboveUnitContent, .timelineReportHeader, .fbTimelineScrubber, .videoUnit, .externalShareUnit
{background-color: transparent !important; border:0px}

#fbPhotoSnowboxFeedback .uiUfi, .fbSettingsEditor,.mlm, .fbApplicationsListItem .uiBoxGray
{opacity:1.0 !important}

#pagelet_friendlist_sidecol
{visibility: visible !important}


body{
background-image:url('http://yancie.netai.net/fb/bg.jpg') !important;
background-repeat: none !important;
background-attachment: fixed !important;
background-position: 70% -20% !important;
background-size:80% !important;
}

#contentCol, .unread,.uiUfiLike,.uiUfiViewAll, .uiUfiComment, .uiUfiAddComment, .uiBoxGray,.timelineUnitContainer,#pagelet_timeline_nav,.fbTimelineNavigationWrapper, .topBorder, .fbTimelineFeedbackActions, .bottomBorder, .timelineReportContainer, #timelineNavContent, .uiUfiViewReposts
 {
background-image:url('http://yancie.netai.net/fb/tran1.png') !important;
background-color: transparent !important;
background-repeat: repeat !important;
border:none !important;
}

 .stickyHeaderWrap .back, .uiButtonGroupItem
{
background-image:url('http://yancie.netai.net/fb/tran6.png') !important;
background-color: transparent !important;
background-repeat: repeat !important;
}

body .fbTimelineViewingSelf , .timelineLayout{
background-image:url('http://yancie.netai.net/fb/TimeBG.png') !important;
background-repeat: none !important;
background-attachment: fixed !important;
background-position: 160% -20% !important;
background-size:90% !important;
}



}

@-moz-document url-prefix("http://www.facebook.com/profile.php") {
body {
background-image:url('http://yancie.netai.net/fb/TimeBG.png') !important;
background-repeat: none !important;
background-attachment: fixed !important;
background-position: 160% -20% !important;
background-size:90% !important;
}
}
